00310         std::vector<std::vector<double>> vMap(5); // X, Y, Z, Flux, ErrorFlux
00311         std::vector<std::vector<double>> vMinMax{ {0,0}, {0,0}, {0,0}};
00312         
00313         std::getline (myfile,strLine); // remove 1st line
00314         while(std::getline (myfile,strLine)) {
00315             std::size_t lastPos = strLine.find(separator, 0), previousPos=0;
00316             for(std::size_t i = 0; i < 5; ++i) {
00317                 vMap[i].push_back( strLine.substr(previousPos, lastPos-previousPos) );
00318                 if(i<3) {
00319                     if(strLine.substr(previousPos, lastPos-previousPos) < vMinMax[i][0])
00320                         vMinMax[i][0] = strLine.substr(previousPos, lastPos-previousPos);
00321                     if(strLine.substr(previousPos, lastPos-previousPos) > vMinMax[i][1])
00322                         vMinMax[i][1] = strLine.substr(previousPos, lastPos-previousPos);
00323                 }
00324                 previousPos = lastPos+1;
00325                 lastPos = strLine.find(separator, previousPos);
00326             }
00327         }
00328         myfile.close();    
00329         
00330 //         TH2D* MappingXY_FluxN_MCNP = new TH2D(str_CanvaName.c_str(), "", vMap[0], vMap[1]);
