00123 void TMXTargetRez3::createGeometry() {  // This method should be called in the constructor after calling createMaterial
00124 //     std::cout << "Beginning of Target definition" << std::endl;
00125 
00126     m_vectorTargetThickness = m_p->src_slabThicknesses; // Thickness of target slabs in our experiment
00127 
00129     // Re-definition of target slabs name
00130     std::string slabName;
00131     if( m_targetName.size() > 0) { // Last element is a generic name added by the constructor
00132         slabName = m_targetName[m_targetName.size()-1]; // save generic name
00133         m_targetName.pop_back();} // remove generic name from the vector of names
00134     for(int i=0; i<(int)m_vectorTargetThickness.size(); ++i) { m_targetName.push_back(slabName+ std::to_string(i)); }
00135     for(int i=0; i<(int)m_vectorTargetThickness.size(); ++i) { m_targetName.push_back(m_assemblyName + "_SlabCoating"+ std::to_string(i)); }
00137 
00138     G4double targetPipeThickness = 5*mm;
00139     G4double targetPipeHeight = m_p->zTargetHeight + targetPipeThickness; // Tube height along X-axis     // checkgap = thickness of wall // conic target
00140     if(m_vectorTargetThickness.size() > 0) {targetPipeHeight = 2*m_vectorTargetThickness[0] +targetPipeThickness;} // Tube height along X-axis     // checkgap = thickness of wall // Cylindrical target
00141 
00142     G4double targetPipeWidth  = 10*cm; // Tube width  along Y-axis = diameter
00143 
00144     // Define solid volumes of target tube
00145     m_cladSolidVolumes.push_back( new G4Tubs("s"+m_cladName[m_cladName.size()-1], 0., 0.5*targetPipeWidth, 0.5*targetPipeHeight, 0., twopi));  // Half size X,Y,Z
00146     m_Solids.push_back( m_cladSolidVolumes[0] ); // Largest volume surrounding all the geometry -> to be substracted to the rest of the geometry when placed
00147 
00148     // Define solid volumes of coolant
00149     m_coolantSolidVolumes.push_back( new G4Tubs("s"+m_coolantName[m_coolantName.size()-1], 0.5 * m_p->yTarget + targetPipeThickness, 0.5 * targetPipeWidth - targetPipeThickness, 0.5*(targetPipeHeight - targetPipeThickness), 0., twopi));  // Half size X,Y,Z
00150     m_coolantName.push_back("Vacuum");
00151     m_coolantSolidVolumes.push_back( new G4Tubs("s"+m_coolantName[m_coolantName.size()-1], 0., 0.5*m_p->yTarget, 0.5*(targetPipeHeight - targetPipeThickness), 0., twopi));  // Half size X,Y,Z
00152 
00153     // Cylindrical target
00154     if(m_vectorTargetThickness.size() > 0) {
00155         m_targetSolidVolumes.push_back( new G4Tubs("s" + m_targetName[0], 0., 0.5 * m_p->yTarget, 0.5 * m_vectorTargetThickness[0], 0., twopi) ); }
00156 
00157 //     std::cout << "End of Target definition" << std::endl;
00158 }
