00160 G4ThreeVector TMXTargetRez3::placeGeometry(bool overlap, G4ThreeVector* centerTarget, G4ThreeVector* beamDirection, G4LogicalVolume* masterVolume) {
00161     G4RotationMatrix* RotationFromXaxis = TMXNeutronSource::DefineRotationMatrix(beamDirection); // Definition of rotation matrix from beamDirection
00162     placeTube(overlap, centerTarget, RotationFromXaxis, masterVolume);        // Placement of tube in the masterVolume (world ?)
00163     placeCoolant(overlap, nullptr, nullptr, m_cladLogicalVolumes[m_cladLogicalVolumes.size()-1]);
00164     placeVacuum(overlap, nullptr, nullptr, m_cladLogicalVolumes[m_cladLogicalVolumes.size()-1]);         // Placement of coolant in tube
00165     placeSlabs  (overlap, nullptr, nullptr, m_coolantLogicalVolumes[m_coolantLogicalVolumes.size()-1]);      // Placement of slabs in coolant
00166     ++m_copy_number;
00167 
00168 //     G4ThreeVector distanceToOut{-0.5 * m_p->zTargetHeight,0,0}; // Conic target
00169     G4ThreeVector distanceToOut{-1.5*m_vectorTargetThickness[0],0,0}; // Cyclinder target
00170     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00171     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00172     if (RotationFromXaxisInverse) { distanceToOut = *RotationFromXaxisInverse * distanceToOut; }  // If rotation, distanceToOut along x axis is rotated
00173     return distanceToOut; // return distance from the target center to the beam window
00174 }
00175 
00176 void TMXTargetRez3::placeSlabs(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00177     size_t index = getFirstOccurence(&m_targetName);
00178     if(m_targetSolidVolumes.size() > 0) {    // Target slabs placement
00179         // Definition of target slab logical volumes
00180         m_targetLogicalVolumes.push_back( new G4LogicalVolume(m_targetSolidVolumes[0], m_TargetMaterial,  "l"+m_targetName[ index ]+std::to_string(m_copy_number) )); // Material : tungsten
00181 
00182 //         G4ThreeVector pos(0 ,0 ,0.5*m_vectorTargetThickness[0]); // conic slab pos
00183 //         G4double targetPipeThickness = 5*mm;
00184         G4ThreeVector pos(0 ,0 ,0.5*m_vectorTargetThickness[0]); // cylinder slab pos
00185         G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00186         if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00187         if (RotationFromXaxisInverse) { pos = *RotationFromXaxisInverse * pos; }  // If rotation, distanceToOut along x axis is rotated
00188         if (centerTarget) {pos = *centerTarget + pos;}
00189         new G4PVPlacement (RotationFromXaxis, pos, m_targetLogicalVolumes[m_targetLogicalVolumes.size()-1], "p" + m_targetName[index], masterVolume, false, m_copy_number, overlap);
00190     }
00191 }
