00193 void TMXTargetRez3::placeCoolant(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00194     size_t index = getFirstOccurence(&m_coolantName);
00195 
00196     // Define logical volumes ofcoolant
00197     m_coolantLogicalVolumes.push_back( new G4LogicalVolume(m_coolantSolidVolumes[0], m_CoolantMaterial, "l"+m_coolantName[index]+std::to_string(m_copy_number)));   // Material : water
00198 
00199     // Place the volume
00200     G4double targetPipeThickness = 5*mm;
00201     G4ThreeVector pos(0., 0., -0.5*targetPipeThickness);
00202     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00203     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00204     if (RotationFromXaxisInverse) { pos = *RotationFromXaxisInverse * pos; }  // If rotation, distanceToOut along x axis is rotated
00205     if (centerTarget) {pos = *centerTarget + pos;}
00206     new G4PVPlacement (RotationFromXaxis, pos, m_coolantLogicalVolumes[0], "p" + m_coolantName[index] , masterVolume, false, m_copy_number, overlap);
00207 }
