00209 void TMXTargetRez3::placeVacuum(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00210     size_t index = getFirstOccurence(&m_coolantName);
00211 
00212     // Define logical volumes ofcoolant
00213     m_coolantLogicalVolumes.push_back( new G4LogicalVolume(m_coolantSolidVolumes[m_coolantSolidVolumes.size()-1], m_vacuum, "l"+m_coolantName[index + 1]+std::to_string(m_copy_number)));   // Material : water
00214 
00215     // Place the volume
00216     G4double targetPipeThickness = 5*mm;
00217     G4ThreeVector pos(0., 0., -0.5*targetPipeThickness);
00218     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00219     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00220     if (RotationFromXaxisInverse) { pos = *RotationFromXaxisInverse * pos; }  // If rotation, distanceToOut along x axis is rotated
00221     if (centerTarget) {pos = *centerTarget + pos;}
00222     new G4PVPlacement (RotationFromXaxis, pos, m_coolantLogicalVolumes[m_coolantSolidVolumes.size()-1], "p" + m_coolantName[index + 1] , masterVolume, false, m_copy_number, overlap);
00223 }
00224 
00225 void TMXTargetRez3::placeTube(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00226     size_t index = getFirstOccurence(&m_cladName);
00227 
00228     // Define logical volumes of target tube
00229     m_cladLogicalVolumes.push_back( new G4LogicalVolume(m_cladSolidVolumes[0], m_CladMaterial, "l"+m_cladName[index]+std::to_string(m_copy_number)));  // Material : stainless steel
00230 
00231     // Place the volume
00232     G4double targetPipeThickness = 5*mm;
00233     G4ThreeVector* pos = new G4ThreeVector(m_p->XtargetPosition-0.5*(m_vectorTargetThickness[0]-targetPipeThickness),0,0);
00234     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00235     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00236     if (RotationFromXaxisInverse) { *pos = *RotationFromXaxisInverse * *pos; }  // If rotation, distanceToOut along x axis is rotated
00237     if (centerTarget) {*pos = *centerTarget + *pos;}
00238     G4RotationMatrix* Rotation = new G4RotationMatrix();
00239     Rotation->rotateY(-CLHEP::pi / 2.);
00240     if (RotationFromXaxis) { *Rotation = (*Rotation) * (*RotationFromXaxis); }
00241     new G4PVPlacement (Rotation, *pos, m_cladLogicalVolumes[m_cladLogicalVolumes.size()-1], "p" + m_cladName[index] , masterVolume, false, m_copy_number, overlap);
00242 
00243     if(m_copy_number != 0) { m_Solids.push_back( m_Solids[0] ); }
00244     m_translation.push_back( pos );
00245     m_rotation.push_back( Rotation );
00246 }
