00063 G4RotationMatrix* TMXNeutronSource::DefineRotationMatrix(G4ThreeVector* beamDirection) {
00064     if(beamDirection->y() != 0 || beamDirection->z() != 0) { 
00065         G4ThreeVector axis = beamDirection->cross(G4ThreeVector(1,0,0));
00066         return new G4RotationMatrix(axis, acos(beamDirection->unit().x())); }  // Vector of rotation and scalar product to find the angle
00067     else if ( beamDirection->x() < 0 ) { // 180 degree rotation around Z
00068         G4RotationMatrix *Rot = new G4RotationMatrix();
00069         Rot->rotateZ(M_PI*rad);
00070         return Rot;
00071     }
00072     return nullptr;
00073 }
