00199 void TMXNeutronSource::placeGeometry(bool overlap, G4ThreeVector* centerTarget, G4ThreeVector* beamDirection, G4LogicalVolume* masterVolume){
00200     // Target
00201     G4ThreeVector DistanceToOut = m_NeutronEmmiter->placeGeometry(overlap, centerTarget, beamDirection, masterVolume);
00202     std::cout << "target placed" << std::endl;
00203 
00204     // Beam pipe
00205      if( m_BeamPipe ) {
00206          if(centerTarget) { DistanceToOut += *centerTarget; }
00207          m_BeamPipe->placeGeometry(overlap, &DistanceToOut, beamDirection, masterVolume);
00208          std::cout << "beam pipe placed" << std::endl;
00209 
00210      }
00211 
00212 }
