00225 G4VSolid* TMXNeutronSource::SubstractTarget(G4VSolid* SolidToBeDrilled, G4ThreeVector translation, G4RotationMatrix* rot) {
00226     std::vector < G4VSolid* > vSolids;
00227     std::vector < G4ThreeVector* > vTranslations;
00228     std::vector < G4RotationMatrix* > vRotations;
00229     std::string strName = SolidToBeDrilled->GetName();
00230 
00231     // Extract solids / Rotations / Translations from target to be subtracted
00232     if ( this->getNeutronEmmiter() ) {
00233         vSolids = *this->getNeutronEmmiter()->getSolids();
00234         vTranslations = *this->getNeutronEmmiter()->getTranslations();
00235         vRotations = *this->getNeutronEmmiter()->getRotations();
00236     }
00237 
00238     // Extract solids / Rotations / Translations from beam pipe to be subtracted
00239     if ( this->getBeamPipe() ) {
00240         vSolids.insert(vSolids.end()            , (*this->getBeamPipe()->getSolids()).begin()       , (*this->getBeamPipe()->getSolids()).end()       );
00241         vTranslations.insert(vTranslations.end(), (*this->getBeamPipe()->getTranslations()).begin() , (*this->getBeamPipe()->getTranslations()).end() );
00242         vRotations.insert(vRotations.end()      , (*this->getBeamPipe()->getRotations()).begin()   , (*this->getBeamPipe()->getRotations()).end()   );
00243     }
00244 
00245     // Substraction of solids
00246     for(size_t i=0; i<vSolids.size(); ++i) {
00247         if(rot) {
00248             G4RotationMatrix* rotation = new G4RotationMatrix();
00249             if(vRotations[i]) { *rotation = (*(vRotations[i])) * (*rot);}
00250             else { *rotation = *rot; }
00251             G4RotationMatrix* InverseRot = new G4RotationMatrix( rot->axis(), -rot->delta());
00252             SolidToBeDrilled = new G4SubtractionSolid( strName + "-sub" + std::to_string(i), SolidToBeDrilled, vSolids[i], rotation, ((*InverseRot) * (*(vTranslations[i]))) - translation);
00253         }
00254         else { SolidToBeDrilled = new G4SubtractionSolid( strName + "-sub" + std::to_string(i), SolidToBeDrilled, vSolids[i], vRotations[i], (*(vTranslations[i]) - translation));  }
00255     }
00256 
00257     return SolidToBeDrilled;
00258 
00259 }
