00058 //------------------------------------------------------------------------------*------------------------------------------------------------------------------//
00059 // Construction of the material compositions
00060 //------------------------------------------------------------------------------*------------------------------------------------------------------------------//
00061 void TMXFuelAssemblyVVRM2::createMaterial()
00062 {
00063   G4double temperature_fuel = m_p->TemperatureFuel;
00064 
00065   G4double massUO2     = 0.313;
00066   G4double massAl      = 1. - massUO2; // 0.687
00067   //G4double X_vol_Al    = 0.888;
00068   G4double massU235pc  = 0.1975;
00069   G4double massU238pc  = 1. - massU235pc; // 0.8025
00070 
00072   // Dioxyde Uranium - Enrichement 19.7% U235
00073   TMXMaterial* _VVR_M2 = new TMXMaterial("VVR-M2 "+m_assemblyName, 9.8*(g/cm3), 4, kStateSolid, temperature_fuel); // !!! Temperature
00074   _VVR_M2->AddElement(922350, massUO2 * 0.881 * massU235pc, "U_235");
00075   _VVR_M2->AddElement(922380, massUO2 * 0.881 * massU238pc, "U_238");
00076   _VVR_M2->AddElement( 80160, massUO2 * 0.119,              "O_16");
00077   _VVR_M2->AddElement(130270, massAl,                       "Al27");
00078 
00079 
00081   // Demineralised Water - Coolant
00082   TMXMaterial* _DemineralizedWater = new TMXMaterial("Demineralised Water "+m_assemblyName, 1.0*(g/cm3), 2, kStateLiquid, temperature_fuel);
00083   _DemineralizedWater->AddElement(80160, 1, "O_16");
00084   _DemineralizedWater->AddElement(10010, 2, "H_1");
00085 
00087   // Cladding in Aluminum
00088   TMXMaterial* _Aluminium = new TMXMaterial("Aluminium "+m_assemblyName, 2.7*(g/cm3), 1, kStateSolid, temperature_fuel);
00089   _Aluminium->AddElement(130270, 1, "Al27");
00090 
00091 
00092   m_FuelMaterial = _VVR_M2;
00093   m_WaterMaterial = _DemineralizedWater;
00094   m_CladMaterial = _Aluminium;
00095 }
00096 
00097 //------------------------------------------------------------------------------*------------------------------------------------------------------------------//
00098 // Construction of the logical volumes for cladding, Fuel and water in the active area
00099 // IRT-4M-8T fuel assembly
00100 //------------------------------------------------------------------------------*------------------------------------------------------------------------------//
00101 // Sources
00102 // https://www.ipen.br/biblioteca/slr/cel/0059
00103 // https://www.researchgate.net/publication/263485134_Thermal_hydraulic_calculations_of_the_IRT-Sofia_research_reactor_with_leu_fuel_IRT-4M_Sofia
00104 // https://www.researchgate.net/publication/238658698_A_neutronic_feasibility_study_for_LEU_conversion_of_the_IR8_research_reactor/figures?lo=1
00105 //------------------------------------------------------------------------------*------------------------------------------------------------------------------//
00106 void TMXFuelAssemblyVVRM2::createGeometry()
00107 {
00109     // Fuel Design - WWR-M2 LEU Fuel Assembly from Source Doc (KIPT Presentation 7-9 February 2016)
00110     /*
00111     Hexagonal shape
00112     Fuel + Cladding width = 2.5 mm
00113     Fuel width = 1.0 mm
00114     Cladding width = (2.5 - 1.0)/2 = 0.75 mm
00115     FuelCell1 (Tubs): radius outer = 11.0 mm
00116     FuelCell2 (Tubs): radius outer = 22.0 mm
00117     FuelCell3 (Hexa): radius outer = 32.0 mm
00118     Bound (Hexa):   radius outer = 35.0 mm
00119                     radius inner = 32.0 mm
00120     */
00121 
00122     G4bool overlap = false;
00123     if (m_p->Verbose == 1) {
00124         overlap = true;
00125     }
00126 
00130 
00132     const G4double width_clad = 0.75 * mm; // = (2.5 - 1.0)/2
00133     const G4double width_fuel = 1.0 * mm;
00134 
00136     fOuterRadius_assembly = 32.0 / 2. * mm;
00137     const G4double outerRadius_fuelAssembly = fOuterRadius_assembly;
00138     const G4double outerRadius_fuelCell3 = 32.0 / 2. * mm; // Cell = Fuel + Clad
00139     const G4double outerRadius_fuelCell2 = 22.0 / 2. * mm;
00140     const G4double outerRadius_fuelCell1 = 11.0 / 2. * mm;
00141 
00143     const G4double halfHeightAssembly = 50./2. * cm; // Active height of fuel (source: prod medical radio iso)
00144 
00148 
00150     const G4double outerRadius_outerClad3 = outerRadius_fuelCell3;
00151     const G4double innerRadius_outerClad3 = outerRadius_outerClad3 - width_clad;
00152     const G4double outerRadius_fuel3 = innerRadius_outerClad3;
00153     const G4double innerRadius_fuel3 = outerRadius_fuel3 - width_fuel;
00154     const G4double outerRadius_innerClad3 = innerRadius_fuel3;
00155     const G4double innerRadius_innerClad3 = outerRadius_innerClad3 - width_clad;
00156 
00158     const G4double outerRadius_outerClad2 = outerRadius_fuelCell2;
00159     const G4double innerRadius_outerClad2 = outerRadius_outerClad2 - width_clad;
00160     const G4double outerRadius_fuel2 = innerRadius_outerClad2;
00161     const G4double innerRadius_fuel2 = outerRadius_fuel2 - width_fuel;
00162     const G4double outerRadius_innerClad2 = innerRadius_fuel2;
00163     const G4double innerRadius_innerClad2 = outerRadius_innerClad2 - width_clad;
00164 
00166     const G4double outerRadius_outerClad1 = outerRadius_fuelCell1;
00167     const G4double innerRadius_outerClad1 = outerRadius_outerClad1 - width_clad;
00168     const G4double outerRadius_fuel1 = innerRadius_outerClad1;
00169     const G4double innerRadius_fuel1 = outerRadius_fuel1 - width_fuel;
00170     const G4double outerRadius_innerClad1 = innerRadius_fuel1;
00171     const G4double innerRadius_innerClad1 = outerRadius_innerClad1 - width_clad;
00172 
00173 
00177 
00178     G4Polyhedra* sMaster = new G4Polyhedra(
00179         "sMaster",        // name
00180         0.,                 // phiStart
00181         360. * deg,         // phiTotal
00182         6,                  // numSide
00183         2,                  // numZPlanes
00184         new double [2]{-halfHeightAssembly, halfHeightAssembly}, // zPlane[]
00185         new double [2]{0., 0.},                                // rInner[]
00186         new double [2]{outerRadius_fuelAssembly, outerRadius_fuelAssembly}    // rOuter[]
00187     );
00188     m_Solids.push_back(sMaster);
00189 
00190     m_masterVolume = new G4LogicalVolume(sMaster, m_WaterMaterial, "lMaster");
00191     m_waterLogicalVolumes.push_back(m_masterVolume);
00192 
00196     // Outer Clad
00197     G4Polyhedra* sCladOuter3 = new G4Polyhedra(
00198         "sMaster",        // name
00199         0.,                 // phiStart
00200         360. * deg,         // phiTotal
00201         6,                  // numSide
00202         2,                  // numZPlanes
00203         new double [2]{-halfHeightAssembly, halfHeightAssembly},      // zPlane[]
00204         new double [2]{innerRadius_outerClad3, innerRadius_outerClad3}, // rInner[]
00205         new double [2]{outerRadius_outerClad3, outerRadius_outerClad3}  // rOuter[]
00206     );
00207     m_Solids.push_back(sCladOuter3);
00208 
00209     G4LogicalVolume* lCladOuter3 = new G4LogicalVolume(sCladOuter3, m_CladMaterial, "lCladOuter3");
00210     m_cladLogicalVolumes.push_back(lCladOuter3);
00211 
00212     new G4PVPlacement( 0, G4ThreeVector(), lCladOuter3, "pCladOuter3", m_masterVolume, false, 0, overlap );
00213 
00215     // Fuel
00216     G4Polyhedra* sFuel3 = new G4Polyhedra(
00217         "sMaster",        // name
00218         0.,                 // phiStart
00219         360. * deg,         // phiTotal
00220         6,                  // numSide
00221         2,                  // numZPlanes
00222         new double [2]{-halfHeightAssembly, halfHeightAssembly},      // zPlane[]
00223         new double [2]{innerRadius_fuel3, innerRadius_fuel3}, // rInner[]
00224         new double [2]{outerRadius_fuel3, outerRadius_fuel3}  // rOuter[]
00225     );
00226     m_Solids.push_back(sFuel3);
00227 
00228     G4LogicalVolume* lFuel3 = new G4LogicalVolume(sFuel3, m_FuelMaterial, "lFuel3");
00229     m_fuelLogicalVolumes.push_back(lFuel3);
00230 
00231     new G4PVPlacement( 0, G4ThreeVector(), lFuel3, "pFuel3", m_masterVolume, false, 0, overlap );
00232 
00234     // Inner Clad
00235     G4Polyhedra* sCladInner3 = new G4Polyhedra(
00236         "sMaster",        // name
00237         0.,                 // phiStart
00238         360. * deg,         // phiTotal
00239         6,                  // numSide
00240         2,                  // numZPlanes
00241         new double [2]{-halfHeightAssembly, halfHeightAssembly},      // zPlane[]
00242         new double [2]{innerRadius_innerClad3, innerRadius_innerClad3}, // rInner[]
00243         new double [2]{outerRadius_innerClad3, outerRadius_innerClad3}  // rOuter[]
00244     );
00245     m_Solids.push_back(sCladInner3);
00246 
00247     G4LogicalVolume* lCladInner3 = new G4LogicalVolume(sCladInner3, m_CladMaterial, "lCladInner3");
00248     m_cladLogicalVolumes.push_back(lCladInner3);
00249 
00250     new G4PVPlacement( 0, G4ThreeVector(), lCladInner3, "pCladInner3", m_masterVolume, false, 0, overlap );
00251 
00252 
00256     // Outer Clad
00257     G4Tubs* sCladOuter2 = new G4Tubs(
00258         "sCladOuter2",          // name
00259         innerRadius_outerClad2, // RMin
00260         outerRadius_outerClad2, // RMax
00261         halfHeightAssembly,  // Dz
00262         0.,                     // SPhi
00263         360. * deg              // DPhi
00264     );
00265     m_Solids.push_back(sCladOuter2);
00266 
00267     G4LogicalVolume* lCladOuter2 = new G4LogicalVolume(sCladOuter2, m_CladMaterial, "lCladOuter2");
00268     m_cladLogicalVolumes.push_back(lCladOuter2);
00269 
00270     new G4PVPlacement( 0, G4ThreeVector(), lCladOuter2, "pCladOuter2", m_masterVolume, false, 0, overlap );
00271 
00273     // Fuel
00274     G4Tubs* sFuel2 = new G4Tubs(
00275         "sFuel2",          // name
00276         innerRadius_fuel2, // RMin
00277         outerRadius_fuel2, // RMax
00278         halfHeightAssembly,  // Dz
00279         0.,                     // SPhi
00280         360. * deg              // DPhi
00281     );
00282     m_Solids.push_back(sFuel2);
00283 
00284     G4LogicalVolume* lFuel2 = new G4LogicalVolume(sFuel2, m_FuelMaterial, "lFuel2");
00285     m_fuelLogicalVolumes.push_back(lFuel2);
00286 
00287     new G4PVPlacement( 0, G4ThreeVector(), lFuel2, "pFuel2", m_masterVolume, false, 0, overlap );
00288 
00290     // Inner Clad
00291     G4Tubs* sCladInner2 = new G4Tubs(
00292         "sCladOuter2",          // name
00293         innerRadius_innerClad2, // RMin
00294         outerRadius_innerClad2, // RMax
00295         halfHeightAssembly,  // Dz
00296         0.,                     // SPhi
00297         360. * deg              // DPhi
00298     );
00299     m_Solids.push_back(sCladInner2);
00300 
00301     G4LogicalVolume* lCladInner2 = new G4LogicalVolume(sCladInner2, m_CladMaterial, "lCladInner2");
00302     m_cladLogicalVolumes.push_back(lCladInner2);
00303 
00304     new G4PVPlacement( 0, G4ThreeVector(), lCladInner2, "pCladInner2", m_masterVolume, false, 0, overlap );
00305 
00306 
00310     // Outer Clad
00311     G4Tubs* sCladOuter1 = new G4Tubs(
00312         "sCladOuter1",          // name
00313         innerRadius_outerClad1, // RMin
00314         outerRadius_outerClad1, // RMax
00315         halfHeightAssembly,  // Dz
00316         0.,                     // SPhi
00317         360. * deg              // DPhi
00318     );
00319     m_Solids.push_back(sCladOuter1);
00320 
00321     G4LogicalVolume* lCladOuter1 = new G4LogicalVolume(sCladOuter1, m_CladMaterial, "lCladOuter1");
00322     m_cladLogicalVolumes.push_back(lCladOuter1);
00323 
00324     new G4PVPlacement( 0, G4ThreeVector(), lCladOuter1, "pCladOuter1", m_masterVolume, false, 0, overlap );
00325 
00327     // Fuel
00328     G4Tubs* sFuel1 = new G4Tubs(
00329         "sFuel1",          // name
00330         innerRadius_fuel1, // RMin
00331         outerRadius_fuel1, // RMax
00332         halfHeightAssembly,  // Dz
00333         0.,                     // SPhi
00334         360. * deg              // DPhi
00335     );
00336     m_Solids.push_back(sFuel1);
00337 
00338     G4LogicalVolume* lFuel1 = new G4LogicalVolume(sFuel1, m_FuelMaterial, "lFuel1");
00339     m_fuelLogicalVolumes.push_back(lFuel1);
00340 
00341     new G4PVPlacement( 0, G4ThreeVector(), lFuel1, "pFuel1", m_masterVolume, false, 0, overlap );
00342 
00344     // Inner Clad
00345     G4Tubs* sCladInner1 = new G4Tubs(
00346         "sCladOuter2",          // name
00347         innerRadius_innerClad1, // RMin
00348         outerRadius_innerClad1, // RMax
00349         halfHeightAssembly,  // Dz
00350         0.,                     // SPhi
00351         360. * deg              // DPhi
00352     );
00353     m_Solids.push_back(sCladInner1);
00354 
00355     G4LogicalVolume* lCladInner1 = new G4LogicalVolume(sCladInner1, m_CladMaterial, "lCladInner1");
00356     m_cladLogicalVolumes.push_back(lCladInner1);
00357 
00358     new G4PVPlacement( 0, G4ThreeVector(), lCladInner1, "pCladInner1", m_masterVolume, false, 0, overlap );
00359 }
