00361 void TMXFuelAssemblyVVRM2::placeGeometry(bool overlap, G4ThreeVector position, G4RotationMatrix* rotation, G4LogicalVolume* masterVolume) {
00362     new G4PVPlacement(rotation, position, m_masterVolume, "pFuelAssemblyVVRM2", masterVolume, false, 0, overlap);
00363 }
