00365 void TMXFuelAssemblyVVRM2::visualisationConfig() {
00366     //if(m_p->Verbose == 1) { std::cout << "\nConfiguration of the visualisation" << std::endl;}
00367 
00369     // visualisation colors configured by DL
00371     G4Colour  black   ( .0      ,  .0        ,  .0        ); // black
00372     G4Colour  lblack  ( .0      ,  .0        ,  .0        , 0.6); // light black
00373     G4Colour  vlblack ( .0      ,  .0        ,  .0        , 0.2); // very light black
00374     G4Colour  white   (1.0      , 1.0        , 1.0        , 0.3); // white
00375     G4Colour  grayD3l (211      , 211        , 211             ); // gray D3 (light)
00376     G4Colour  grayC   (0.5      , 0.5        , 0.5             ); // gray
00377     G4Colour  flgrayC (0.5      , 0.5        , 0.5        , 0.8); // few light gray
00378     G4Colour  lgrayC  (0.5      , 0.5        , 0.5        , 0.4); // light gray
00379     G4Colour  vlgrayC (0.5      , 0.5        , 0.5        , 0.2); // very ligth gray
00380     G4Colour  red     (1.0      , 0.0        , 0.0             ); // red
