00040 void SVBR100_FuelPin::createGeometry() {
00041     bool overlap = m_p->Verbose >= 1;
00042 
00043     createMaterials();
00044 
00045     G4double halfHeight_topReflector = 65./2. * mm;
00046     G4double halfHeight_activeCore = 900./2. * mm;
00047     G4double halfHeight_bottomReflector = 150./2. * mm;
00048     G4double halfHeight_pin = halfHeight_topReflector + halfHeight_activeCore + halfHeight_bottomReflector;
00049 
00050     G4double zPosition_topReflector = halfHeight_pin - halfHeight_topReflector;
00051     G4double zPosition_activeCore = zPosition_topReflector - halfHeight_topReflector - halfHeight_activeCore;
00052     G4double zPosition_bottomReflector = -halfHeight_pin + halfHeight_bottomReflector;
00053 
00054     // Cladding
00055     G4double outerRadius_cladding = 12.0/2. * mm;
00056     G4double thickness_cladding = 0.4 * mm;
00057     // Gap
00058     G4double outerRadius_gap = outerRadius_cladding - thickness_cladding;
00059     G4double thickness_gap = 0.1 * mm;
00060     // Fuel
00061     G4double outerRadius_fuel = outerRadius_gap - thickness_gap;
00062     // Void
00063     G4double outerRadius_void = 1.1/2. * mm;
00064 
00065     G4double halfHeight_replica = halfHeight_activeCore/(double)m_nbSections;
00066 
00067     //--------------------------------------------------------------------------*
00068 
00069     // std::string fuelA_round = m_fuelAssemblyIndex != -1 ? "_Round"+std::to_string(m_fuelAssemblyIndex) : "";
00070 
00071     // Cladding
00072     G4Tubs* sCladding = new G4Tubs("s"+m_name+"_Cladding", 0., outerRadius_cladding, halfHeight_pin, 0., twopi);
00073     G4LogicalVolume* lCladding = new G4LogicalVolume(sCladding, m_claddingMaterial, "l"+m_name+"_Cladding");
00074 
00075     // Void
00076     G4Tubs* sVoid = new G4Tubs("s"+m_name+"_Void", 0., outerRadius_gap, halfHeight_pin, 0., twopi);
00077     G4LogicalVolume* lVoid = new G4LogicalVolume(sVoid, m_voidMaterial, "l"+m_name+"_Void");
00078     new G4PVPlacement(0, G4ThreeVector(), lVoid, "p"+m_name+"_Void", lCladding, false, 0, overlap);
00079 
00080     // Top Reflector
00081     G4Tubs* sTopReflector = new G4Tubs("s"+m_name+"_topReflector", outerRadius_void, outerRadius_fuel, halfHeight_topReflector, 0., twopi);
00082     G4LogicalVolume* lTopReflector = new G4LogicalVolume(sTopReflector, m_reflectorMaterial, "l"+m_name+"_topReflector");
00083     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_topReflector), lTopReflector, "p"+m_name+"_topReflector", lVoid, false, 0, overlap);
00084 
00085     // Fuel
00086     G4Tubs* sFuel = new G4Tubs("s"+m_name+"_Fuel", outerRadius_void, outerRadius_fuel, halfHeight_replica, 0., twopi);
00087     // G4LogicalVolume* lFuelPellet = new G4LogicalVolume(sFuel, m_fuelMaterial, "lFuelPin_FuelPellet");
00088     // new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_activeCore), lFuelPellet, "pFuelPin_FuelPellet", lVoid, false, 0, overlap);
00089 
00090     // Bottom Reflector
00091     G4Tubs* sBottomReflector = new G4Tubs("s"+m_name+"_bottomReflector", outerRadius_void, outerRadius_fuel, halfHeight_bottomReflector, 0., twopi);
00092     G4LogicalVolume* lBottomReflector = new G4LogicalVolume(sBottomReflector, m_reflectorMaterial, "l"+m_name+"_bottomReflector");
00093     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_bottomReflector), lBottomReflector, "p"+m_name+"_bottomReflector", lVoid, false, 0, overlap);
00094 
00095     if (m_isReplicated) {
00096         /*G4Tubs* sFuelPin = new G4Tubs("sFuelPin", 0., outerRadius_cladding, halfHeight_activeCore, 0., twopi);
00097         G4LogicalVolume* lFuelPin = new G4LogicalVolume(sFuelPin, m_fuelMaterial, "lFuelPin");
00098         m_masterVolume = lFuelPin;*/
00099 
00100         std::string name;
00101         for (int i = 0; i < m_nbSections; i++) {
00102             name = m_name+"_FuelPellet_"+std::to_string(i);
00103             G4LogicalVolume* lFuelPellet = new G4LogicalVolume(sFuel, m_fuelMaterialVector[i], "l"+name);
00104             new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_activeCore + halfHeight_replica * (m_nbSections - 2.*i - 1)), lFuelPellet, "p"+name, lVoid, false, i, overlap);
00105         }
00106 
00107         // new G4PVReplica("pFuelPin_replica", lFuelPin, lCladding, kZAxis, nSections, halfHeight_replica);
00108     }
00109     else {
00110         G4LogicalVolume* lFuelPellet = new G4LogicalVolume(sFuel, m_fuelMaterialVector[0], "l"+m_name+"_FuelPellet");
00111         new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_activeCore), lFuelPellet, "p"+m_name+"_FuelPellet", lVoid, false, 0, overlap);
00112     }
00113     m_masterVolume = lCladding;
00114 }
00115 
00116 void SVBR100_FuelPin::createMaterials() {
00117     G4double temperature_cladding = (300. + 273.15) * kelvin; // !!! Random Temperature
00118     G4double temperature_fuel = 900. * kelvin; // !!! Random TemperatureTemperature
00119     G4double temperature_void = (500. + 237.15) * kelvin; // !!! Random Temperature
00120 
00121     const double A_U235 = 235.044, A_U238 = 238.051, A_O16 = 15.995; // g/mol
00122 
00123     // std::string fuelA_round = m_fuelAssemblyIndex != -1 ? "_Round"+std::to_string(m_fuelAssemblyIndex) : "";
00124 
00126     // Cladding
00127     TMXMaterial* EP823 = new TMXMaterial(
00128         "EP823_"+m_fuelAssemblyIndex, 7.87*(g/cm3), 47, kStateSolid, temperature_cladding // !!! Density to be confirmed
00129     );
00130     {
00131         // C Nat - 2 Stable Isotopes - 0.16 wt%
00132         double frac_C = 0.16e-2;
00133         EP823->AddElement( 60120, frac_C * 0.989); // C-12
00134         EP823->AddElement( 60120, frac_C * 0.011); // C-13
00135         // Mn Nat - 1 Stable Isotope - 0.55 wt%
00136         double frac_Mn = 0.55e-2;
00137         EP823->AddElement(250550, frac_Mn); // Mn55
00138         // P Nat - 1 Stable Isotope - 0.014 wt%
00139         double frac_P = 0.014e-2;
00140         EP823->AddElement(150310, frac_P); // P-31
00141         // S Nat - 4 Stable Isotopes - 0.03 wt%
00142         double frac_S = 0.004e-2;
00143         EP823->AddElement(160320, frac_S * 0.9499); // S-32
00144         EP823->AddElement(160330, frac_S * 0.0075); // S-33
00145         EP823->AddElement(160340, frac_S * 0.0425); // S-34
00146         EP823->AddElement(160360, frac_S * 0.0001); // S-36
00147         // Si Nat - 3 Stable Isotopes - 1.09 wt%
00148         double frac_Si = 1.09e-2;
00149         EP823->AddElement(140280, frac_Si * 0.922); // Si28
00150         EP823->AddElement(140290, frac_Si * 0.047); // Si29
00151         EP823->AddElement(140300, frac_Si * 0.031); // Si30
00152         // Cr Nat - 4 Stable Isotopes - 11.70 wt%
00153         double frac_Cr = 11.70e-2;
00154         EP823->AddElement(240500, frac_Cr * 0.04345); // Cr50
00155         EP823->AddElement(240520, frac_Cr * 0.83789); // Cr52
00156         EP823->AddElement(240530, frac_Cr * 0.09501); // Cr53
00157         EP823->AddElement(240540, frac_Cr * 0.02365); // Cr54
00158         // Ni Nat - 5 Stable Isotopes - 0.66 wt%
00159         double frac_Ni = 0.66e-2;
00160         EP823->AddElement(280580, frac_Ni * 0.68077); // Ni58
00161         EP823->AddElement(280600, frac_Ni * 0.26223); // Ni60
00162         EP823->AddElement(280610, frac_Ni * 0.01140); // Ni61
00163         EP823->AddElement(280620, frac_Ni * 0.03635); // Ni62
00164         EP823->AddElement(280640, frac_Ni * 0.00926); // Ni64
00165         // Mo Nat - 7 Stable Isotopes - 0.74 wt%
00166         double frac_Mo = 0.74e-2;
00167         EP823->AddElement(420920, frac_Mo * 0.1465); // Mo92
00168         EP823->AddElement(420940, frac_Mo * 0.0919); // Mo94
00169         EP823->AddElement(420950, frac_Mo * 0.1587); // Mo95
00170         EP823->AddElement(420960, frac_Mo * 0.1667); // Mo96
00171         EP823->AddElement(420970, frac_Mo * 0.0958); // Mo97
00172         EP823->AddElement(420980, frac_Mo * 0.2429); // Mo98
00173         EP823->AddElement(421000, frac_Mo * 0.0974); // Mo100
00174         // Cu Nat - 2 Stable Isotopes - 0.002 wt%
00175         double frac_Cu = 0.002e-2;
00176         EP823->AddElement(290630, frac_Cu * 0.6915); // Cu63
00177         EP823->AddElement(290650, frac_Cu * 0.3085); // Cu65
00178         // V Nat - 2 Stable Isotopes - 0.30 wt%
00179         double frac_V = 0.30e-2;
00180         EP823->AddElement(230500, frac_V * 0.0025); // V-50
00181         EP823->AddElement(230510, frac_V * 0.9975); // V-51
00182         // W Nat - 5 Stable Isotopes - 0.60 wt%
00183         double frac_W = 0.60e-2;
00184         EP823->AddElement(741800, frac_W * 0.0012); // W-180
00185         EP823->AddElement(741820, frac_W * 0.2650); // W-182
00186         EP823->AddElement(741830, frac_W * 0.1431); // W-183
00187         EP823->AddElement(741840, frac_W * 0.3064); // W-184
00188         EP823->AddElement(741860, frac_W * 0.2843); // W-186
00189         // Nb Nat - 1 Stable Isotope - 0.24 wt%
00190         double frac_Nb = 0.24e-2;
00191         EP823->AddElement(410930, frac_Nb); // Nb93
00192         // B Nat - 2 Stable Isotopes - 0.009 wt%
00193         double frac_B = 0.009e-2;
00194         EP823->AddElement( 50100, frac_B * 0.2); // B-10
00195         EP823->AddElement( 50110, frac_B * 0.8); // B-11
00196         // Ce Nat - 4 Stable Isotopes - 0.04 wt%
00197         double frac_Ce = 0.04e-2;
00198         EP823->AddElement(581360, frac_Ce * 0.00186); // Ce136
00199         EP823->AddElement(581380, frac_Ce * 0.00251); // Ce138
00200         EP823->AddElement(581400, frac_Ce * 0.88449); // Ce140
00201         EP823->AddElement(581420, frac_Ce * 0.11114); // Ce142
00202         // Fe Nat - 4 Stable Isotopes - %
00203         double frac_Fe = 1. - (
00204             frac_C+frac_Mn+frac_P+frac_S+frac_Si+frac_Cr+frac_Ni+frac_Mo+frac_Cu+
00205             frac_V+frac_W+frac_Nb+frac_B+frac_Ce
00206         );
00207         EP823->AddElement(260540, frac_Fe * 0.05845); // Fe54
00208         EP823->AddElement(260560, frac_Fe * 0.91754); // Fe56
00209         EP823->AddElement(260570, frac_Fe * 0.02119); // Fe57
00210         EP823->AddElement(260580, frac_Fe * 0.00282); // Fe58
00211     }
00212 
00214     // Void
00216     // Inert Gas
00217     TMXMaterial* InertGas = new TMXMaterial(
00218         "InertGas_"+m_fuelAssemblyIndex, 0.0017837*(g/cm3), 3, kStateSolid, temperature_void
00219     );
00220     InertGas->AddElement(180360, 0.00334); // Ar36
00221     InertGas->AddElement(180380, 0.00063); // Ar38
00222     InertGas->AddElement(180400, 0.99604); // Ar40
00223 
00225     // Fuel
00226     //-*-*-*-*-*-*
00227     const std::string fuelSelected = "UO2"; // UPuO2
00228     //-*-*-*-*-*-*
00229     TMXMaterial* fuel;
00230     std::string ext;
00231     for (int i = 0; i < m_nbSections; i++) {
00232         ext = "_"+m_fuelAssemblyIndex+"_"+std::to_string(i);
00233         if (fuelSelected == "UPuO2") {
00235             // Fuel UPuO2
00236             double scaleFactor = 10000000.;
00237             const double fracU = 0.794858, fracNp = 0.000047,
00238                         fracPu = 0.200346, fracAm = 0.004749;
00239             fuel = new TMXMaterial(
00240                 "UPuO2_fuel"+ext, 10.407*(g/cm3), 12, kStateSolid, temperature_fuel
00241             );
00242             // Uranium
00243             fuel->AddElement(922340, (int)(0.000043 * fracU  * scaleFactor)); // U_234
00244             fuel->AddElement(922350, (int)(0.005238 * fracU  * scaleFactor)); // U_235
00245             fuel->AddElement(922360, (int)(0.000050 * fracU  * scaleFactor)); // U_236
00246             fuel->AddElement(922380, (int)(0.994669 * fracU  * scaleFactor)); // U_238
00247             // Neptunium
00248             fuel->AddElement(932370, (int)(1.       * fracNp * scaleFactor)); // Np237
00249             // Plutonium
00250             fuel->AddElement(942380, (int)(0.002800 * fracPu * scaleFactor)); // Pu238
00251             fuel->AddElement(942390, (int)(0.708407 * fracPu * scaleFactor)); // Pu239
00252             fuel->AddElement(942400, (int)(0.249273 * fracPu * scaleFactor)); // Pu240
00253             fuel->AddElement(942410, (int)(0.026509 * fracPu * scaleFactor)); // Pu241
00254             fuel->AddElement(942420, (int)(0.013011 * fracPu * scaleFactor)); // Pu242
00255             // Americium
00256             fuel->AddElement(952410, (int)(1.       * fracAm * scaleFactor)); // Am241
00257             // Oxygen
00258             fuel->AddElement( 80160, (int)(2.*(fracU+fracNp+fracPu+fracAm)*scaleFactor)); // O_16
00259         }
00260 
00261         else if (fuelSelected == "UO2") {
00263             // Fuel UO2
00264             double enrichmentU235 = 0.161;
00265             double m_U = A_U235*enrichmentU235 + A_U238*(1.-enrichmentU235), m_O16 = 2.*A_O16;
00266             double m_UO2 = m_O16 + m_U;
00267             fuel = new TMXMaterial(
00268                 "UO2_fuel"+ext, 10.407*(g/cm3), 3, kStateSolid, temperature_fuel
00269             );
00270             // Uranium
00271             fuel->AddElement(922350,   m_U/m_UO2 * (   enrichmentU235)); // U_235
00272             fuel->AddElement(922380,   m_U/m_UO2 * (1.-enrichmentU235)); // U_238
00273             // Oxygen
00274             fuel->AddElement( 80160, m_O16/m_UO2); // O_16
00275         }
00276 
00277         m_fuelMaterialVector.push_back(fuel);
00278     }
00279 
00281     // Fuel UnatO2
00282     double enrichmentU235 = 0.007;
00283     double m_U = A_U235*enrichmentU235 + A_U238*(1.-enrichmentU235), m_O16 = 2.*A_O16;
00284     double m_UO2 = m_O16 + m_U;
00285     TMXMaterial* fuel_UnatO2 = new TMXMaterial(
00286         "UnatO2_"+m_fuelAssemblyIndex, 10.407*(g/cm3), 3, kStateSolid, temperature_fuel
00287     );
00288     // Uranium
00289     fuel_UnatO2->AddElement(922350,   m_U/m_UO2 * (   enrichmentU235)); // U_235
00290     fuel_UnatO2->AddElement(922380,   m_U/m_UO2 * (1.-enrichmentU235)); // U_238
00291     // Oxygen
00292     fuel_UnatO2->AddElement( 80160, m_O16/m_UO2); // O_16
00293 
00295     // Material Selection
00296     m_claddingMaterial = EP823;
00297     // Select fuel, see above, search "fuelSelected"
00298     m_reflectorMaterial = fuel_UnatO2;
00299     m_voidMaterial = InertGas;
00300 }
