00022 void marginCanvas(TCanvas* canvas, double margin) {
00023     canvas->SetLeftMargin(margin);
00024     canvas->SetRightMargin(margin);
00025     canvas->SetBottomMargin(margin);
00026     canvas->SetTopMargin(margin);
00027 }
