00037 int HexagonalEdepMap(std::string inputFile) {
00038     // Reading name of the ROOT file
00039     size_t last_slash = inputFile.rfind("/");
00040     if(last_slash == std::string::npos) {
00041         last_slash = 0;
00042     }
00043     std::string FileName = inputFile.substr(last_slash, inputFile.rfind(".root")-last_slash);
00044     TFile *f_ROOT = new TFile( (inputFile).c_str() );
00045 
00046     //------------------------- Begin User Input -------------------------------*
00047     // Reactor Parameter
00048     double pitch = 225.45; // mm
00049     double a = pitch * 2./sqrt(3.); // mm
00050     double height = 900.; // mm
00051     double volumeAssembly = 3.*sqrt(3.)/8. * a*a * height * 1e-3; // cm3
00052     double volumeFuelAssembly = height * 198. * 3.14159265 * (5.5*5.5-0.55*0.55) * 1e-3;
00053     double volume = volumeFuelAssembly;
00054     double density = 10.407; // g/cm3
00055 
00056     // Physical Scale
00057     double corePower = 280.; // MW
00058     double nNeutrons = 160000.;
00059     double nAssembly = 61.;
00060 
00061     // Binning of Hexagonal Map
00062     int    xBin = 11,                     yBin = 13;
00063     double xMin = -(double)xBin/2.*pitch, yMin = -(3.*(double)yBin+1.)*a/8.;
00064     double xMax = -xMin,                  yMax = -yMin;
00065 
00066     // Plot Scale
00067     double minPowerDeposition =   2.70*0; // MW
00068     double minPowerDensity    = 160.50*0; // W/cm3
00069     double minSpecificPower   =  15.50*0; // W/g
00070     double maxPowerDeposition =   6.88; // MW
00071     double maxPowerDensity    = 410.37; // W/cm3
00072     double maxSpecificPower   =  39.43; // W/g
00073 
00074     // Canvas Setup
00075     double margin = 0.16;
00076     double windowWidth = 1000.;
00077 
00078     bool isSaveHasPNG = true;
00079     //------------------------- End User Input ---------------------------------*
00080 
00081     TTree* DepositedPower = (TTree*)f_ROOT->GetObjectChecked(("DepositedPower"), "TTree");
00082     int nEntries = (int)DepositedPower->GetEntries();
00083 
00084     TH2Poly *hexEnergy = new TH2Poly();
00085     hexEnergy->Honeycomb(xMin, yMin, a/2., xBin, yBin);
00086     hexEnergy->GetXaxis()->SetTitle("X (mm)");
00087     hexEnergy->GetYaxis()->SetTitle("Y (mm)");
00088     hexEnergy->GetZaxis()->SetTitleOffset(1.5);
00089     gStyle->SetCanvasPreferGL(true);
00090     gStyle->SetPaintTextFormat(".2f");
00091     gStyle->SetOptStat(false);
00092 
00093     // TH2D* MappingXY_Edep = GetAndDraw2D_histo(histo[0], "MappingXY_Edeposit_" + FileName, "MappingXY_Edeposit", "X [mm]", "Y [mm]", "Power density [W / cm^{3}] @ " + str_beamPower, rescaling_Data, "colz", save_root, save_png);
00094     TH2D* MappingXY_Edep = (TH2D*)gDirectory->Get("MappingXY_Edeposit");
00095     MappingXY_Edep->Draw("colz");
00096 
00097 
00098     for (int x = 0; x < 13; x++) {
00099         for (int y = 0; y < 13; y++) {
00100             std::cout << MappingXY_Edep->GetBinContent(x, y) << '\n';
00101             hexEnergy->Fill(x, y, MappingXY_Edep->GetBinContent(x, y));
00102         }
00103     }
00104 
00105     hexEnergy->Draw("colz text");
00106 
00107     /*double x, y, edep, Etot = 0;
00108     for (int i = 0; i < nEntries; i++) {
00109         DepositedPower->GetEntry(i);
00110         if (DepositedPower->GetLeaf("isInFuel")->GetValue(0) == 1) {
00111             x = DepositedPower->GetLeaf("NPosX")->GetValue(0);
00112             y = DepositedPower->GetLeaf("NPosY")->GetValue(0);
