00117         }
00118     }
00119 
00120     //--------------------------------------------------------------------------*
00121     std::string name = "Power Deposition";
00122     double scalingFactor = corePower / Etot;
00123 
00124     TCanvas* cPowerDeposition = new TCanvas(name.c_str(), name.c_str(), windowWidth, windowWidth);
00125     marginCanvas(cPowerDeposition, margin);
00126     TH2Poly *hexPowerDeposition = (TH2Poly*)hexEnergy->Clone(name.c_str());
00127     hexPowerDeposition->SetName(name.c_str());
00128     hexPowerDeposition->SetTitle((name + " " + ext).c_str());
00129     hexPowerDeposition->GetZaxis()->SetTitle((name + " (MW)").c_str());
00130     hexPowerDeposition->Scale(scalingFactor);
00131     hexPowerDeposition->GetZaxis()->SetRangeUser(minPowerDeposition, maxPowerDeposition);
00132     hexPowerDeposition->Draw("coll colz text 0");
00133 
00134     std::cout << "Total Energy Deposited : " << Etot << " eV" << std::endl
00135               << "Energy Deposited per neutron : " << Etot*1e-6/nNeutrons << " MeV" << std::endl
00136               << "Total Core Power : " << scalingFactor*Etot << " MW" << std::endl
00137               << "Average Power Deposition : " << totalContent(hexPowerDeposition)/nAssembly << " MW" << std::endl;
00138 
00139     //--------------------------------------------------------------------------*
00140     name = "Power Density";
00141     scalingFactor = corePower*1e6 / (Etot*volume);
00142 
00143     TCanvas* cPowerDensity = new TCanvas(name.c_str(), name.c_str(), windowWidth, windowWidth);
00144     marginCanvas(cPowerDensity, margin);
00145     TH2Poly *hexPowerDensity = (TH2Poly*)hexEnergy->Clone(name.c_str());
00146     hexPowerDensity->SetName(name.c_str());
00147     hexPowerDensity->SetTitle((name + " " + ext).c_str());
00148     hexPowerDensity->GetZaxis()->SetTitle((name + " (W/cm^{3})").c_str());
00149     hexPowerDensity->Scale(scalingFactor);
00150     hexPowerDensity->GetZaxis()->SetRangeUser(minPowerDensity, maxPowerDensity);
00151     hexPowerDensity->Draw("coll colz text 0");
00152 
00153     std::cout << "Average Power Density : " << totalContent(hexPowerDensity)/nAssembly << " W/cm3" << std::endl;
00154 
00155     //--------------------------------------------------------------------------*
00156     name = "Specific Power";
00157     scalingFactor = corePower*1e6 / (Etot*volume*density);
00158     TCanvas* cSpecificPower = new TCanvas(name.c_str(), name.c_str(), windowWidth, windowWidth);
00159     marginCanvas(cSpecificPower, margin);
00160     TH2Poly *hexSpecificPower = (TH2Poly*)hexEnergy->Clone(name.c_str());
00161     hexSpecificPower->SetName(name.c_str());
00162     hexSpecificPower->SetTitle((name + " " + ext).c_str());
00163     hexSpecificPower->GetZaxis()->SetTitle((name + " (W/g)").c_str());
00164     hexSpecificPower->Scale(scalingFactor);
00165     hexSpecificPower->GetZaxis()->SetRangeUser(minSpecificPower, maxSpecificPower);
00166     std::cout << "Average Specific Power : " << totalContent(hexSpecificPower)/nAssembly << " W/g" << std::endl;
00167     hexSpecificPower->Draw("coll colz text 0");
00168 
00169     //--------------------------------------------------------------------------*
00170     if (isSaveHasPNG) {
00171         cPowerDeposition->SaveAs(("Power_Deposition_" + ext + ".png").c_str());
00172         cPowerDensity   ->SaveAs(("Power_Density_" + ext + ".png").c_str());
00173         cSpecificPower  ->SaveAs(("Specific_Power_" + ext + ".png").c_str());
00174     }*/
00175     return(0);
00176 }
