00047 TMXAbstractBeamPipe::TMXAbstractBeamPipe(TMXInputParameters* parameters, const std::string& BeamPipeAssemblyName):
00048 m_assemblyName(BeamPipeAssemblyName),
00049 m_p{parameters},
00050 m_copy_number{0},
00051 m_beamPipeLength{0},
00052 m_WindowThickness{0}
00053 {
00054     m_vacuumName  .push_back( BeamPipeAssemblyName + "_VacuumBeamPipe" );
00055     m_tubeName    .push_back( BeamPipeAssemblyName + "_BeamPipe");
00056     m_windowName  .push_back( BeamPipeAssemblyName + "_BeamWindow");
00057 }
