00093 
00094 void TMXAbstractBeamPipe::createMaterial(double Kelvin_temperature) {
00095     
00096     // Default Materials
00097     
00098     // Vaccum
00099     m_vacuumMaterial = TMXMaterial::Find("G4_Galactic");
00100     if( !m_vacuumMaterial ) {
00101         m_vacuumMaterial = new TMXMaterial("G4_Galactic",CLHEP::universe_mean_density*g/cm3, 1, kStateGas, Kelvin_temperature, 3.e-18*hep_pascal);
00102         m_vacuumMaterial->AddElement(10010,1);
00103     }
00104     
00105     // Stainless steel tube
00106     // Stainless steel cladding of the target
00107     m_tubeMaterial = new TMXMaterial(m_assemblyName + "_beampipe_ssteel",8*g/cm3, 8, kStateSolid, Kelvin_temperature);
00108     m_tubeMaterial->AddElement(260540,99); // Fe-54
00109     m_tubeMaterial->AddElement(260560,33); // Fe-56
00110     m_tubeMaterial->AddElement(260570,84); // Fe-57
00111     m_tubeMaterial->AddElement(260580,43); // Fe-58
00112     m_tubeMaterial->AddElement(240500,83); // Cr-50
00113     m_tubeMaterial->AddElement(240520,23); // Cr-52
00114     m_tubeMaterial->AddElement(240530,15); // Cr-53
00115     m_tubeMaterial->AddElement(240540,16); // Cr-54
00116     
00117     // Vacuum
00118     m_windowMaterial = TMXMaterial::Find("G4_Galactic");
00119     if( !m_windowMaterial ) {
00120         m_windowMaterial = new TMXMaterial("G4_Galactic",CLHEP::universe_mean_density*g/cm3, 1, kStateGas, Kelvin_temperature, 3.e-18*hep_pascal);
00121         m_windowMaterial->AddElement(10010,1);
00122     }
00123 }
