00129 G4Element* TMXAbstractBeamPipe::ElementExists(int Z, double A) {
00130     // Search for existing element by its Z and A
00131     G4ElementTable elemTable = *G4Element::GetElementTable();
00132     for(size_t i = 0; i<elemTable.size(); ++i) {
00133         if (elemTable[i] && elemTable[i]->GetZ() == Z && elemTable[i]->GetA() == A) {
00134             return elemTable[i];
00135         }
00136     }
00137     return nullptr;
00138 }
00139 
00140 G4Element* TMXAbstractBeamPipe::ElementExists(std::string name) {
00141     // Search for existing element by its name
00142     G4ElementTable elemTable = *G4Element::GetElementTable();
00143     for(size_t i = 0; i<elemTable.size(); ++i) {
00144         if (elemTable[i] && (std::string)elemTable[i]->GetName() == name) {
00145             return elemTable[i];
00146         }
00147     }
00148     return nullptr;
00149 }
00150 
00151 size_t TMXAbstractBeamPipe::getFirstOccurence(std::vector<std::string>* nameVector) { // Get first occurence of m_assembly_name
00152     for(size_t i = 0; i < nameVector->size(); ++i) {
00153         if( (*nameVector)[i].find(m_assemblyName + "_") != std::string::npos ) {
00154             return i;
00155         }
00156     }
00157     return 0;
00158 }
