00190 G4ThreeVector TMXTargetEA::placeGeometry(bool overlap, G4ThreeVector* centerTarget, G4ThreeVector* beamDirection, G4LogicalVolume* masterVolume) {
00191     G4RotationMatrix* RotationFromXaxis = TMXNeutronSource::DefineRotationMatrix(beamDirection); // Definition of rotation matrix from beamDirection
00192     placeTube(overlap, centerTarget, RotationFromXaxis, masterVolume);        // Placement of tube in the masterVolume (world ?)
00193     placeCoolant(overlap, nullptr, nullptr, m_cladLogicalVolumes[m_cladLogicalVolumes.size()-2]);         // Placement of coolant in tube
00194     placeSlabs  (overlap, nullptr, nullptr, m_coolantLogicalVolumes[m_coolantLogicalVolumes.size()-1]);      // Placement of slabs in coolant
00195     ++m_copy_number;
00196     
00197     G4double targetPipeThickness = m_p->CheckGap;  
00198     G4ThreeVector distanceToOut{-0.5*m_targetPipeLength + m_p->XtargetPosition + 0.5*targetPipeThickness,0,0};
00199     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00200     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00201     if (RotationFromXaxisInverse) { distanceToOut = *RotationFromXaxisInverse * distanceToOut; }  // If rotation, distanceToOut along x axis is rotated
00202     return distanceToOut;
00203 }
