00205 void TMXTargetEA::placeSlabs(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00206     size_t index = getFirstOccurence(&m_targetName);
00207         
00208         // Definition of target slab + coating logical volumes
00209         m_targetLogicalVolumes .push_back( new G4LogicalVolume(m_targetSolidVolumes [0], m_CladMaterial,  "l"+m_targetName[index]+std::to_string(m_copy_number) )); // Material : SS316L        
00210         m_targetLogicalVolumes .push_back( new G4LogicalVolume(m_targetSolidVolumes [1], m_TargetMaterial,  "l"+m_targetName[index + 1]+std::to_string(m_copy_number) )); // Material : Pb-Bi        
00211         
00212         G4ThreeVector pos(0 ,0 , (2.342 - 0.6675)*m);
00213         G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00214         if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00215         if (RotationFromXaxisInverse) { pos = *RotationFromXaxisInverse * pos; }  // If rotation, distanceToOut along x axis is rotated
00216         if (centerTarget) {pos = *centerTarget + pos;}
00217         
00218         new G4PVPlacement (RotationFromXaxis, pos , m_targetLogicalVolumes[m_targetLogicalVolumes.size()-2], "p" + m_targetName[index]  , masterVolume, false, m_copy_number, overlap);
00219         new G4PVPlacement (0, G4ThreeVector(0,0,0), m_targetLogicalVolumes[m_targetLogicalVolumes.size()-1], "p" + m_targetName[index+1], m_targetLogicalVolumes[m_targetLogicalVolumes.size()-2], false, m_copy_number, overlap);
00220 }
