00222 void TMXTargetEA::placeCoolant(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00223     size_t index = getFirstOccurence(&m_coolantName);
00224 
00225     // Define logical volumes ofcoolant
00226     m_coolantLogicalVolumes.push_back( new G4LogicalVolume(m_coolantSolidVolumes[0], m_CoolantMaterial, "l"+m_coolantName[index]+std::to_string(m_copy_number)));   // Material : water
00227     
00228     // Place the volume
00229     G4ThreeVector pos(0,0,0);
00230     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00231     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00232     if (RotationFromXaxisInverse) { pos = *RotationFromXaxisInverse * pos; }  // If rotation, distanceToOut along x axis is rotated
00233     if (centerTarget) {pos = *centerTarget + pos;}
00234     new G4PVPlacement (RotationFromXaxis, pos, m_coolantLogicalVolumes[0], "p" + m_coolantName[index] , masterVolume, false, m_copy_number, overlap);
00235 }
