00237 void TMXTargetEA::placeTube(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00238     size_t index = getFirstOccurence(&m_cladName);
00239 
00240     // Define logical volumes of target tube
00241     m_cladLogicalVolumes.push_back( new G4LogicalVolume(m_cladSolidVolumes[0], m_CladMaterial, "l"+m_cladName[index]+std::to_string(m_copy_number)));  // Material : stainless steel
00242 //     m_cladLogicalVolumes.push_back( new G4LogicalVolume(m_cladSolidVolumes[0], m_CoatingMaterial, "l"+m_cladName[index]+std::to_string(m_copy_number)));  // Material : stainless steel
00243 
00244     // Place the volume
00245     G4double targetPipeThickness = m_p->CheckGap;  
00246     G4ThreeVector* pos = new G4ThreeVector(m_p->XtargetPosition+0.5*targetPipeThickness,0,0);
00247     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00248     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00249     if (RotationFromXaxisInverse) { *pos = *RotationFromXaxisInverse * *pos; }  // If rotation, distanceToOut along x axis is rotated
00250     if (centerTarget) {*pos = *centerTarget + *pos;}
00251     new G4PVPlacement (RotationFromXaxis, *pos, m_cladLogicalVolumes[m_cladLogicalVolumes.size()-1], "p" + m_cladName[index] , masterVolume, false, m_copy_number, overlap);
00252     
00253     if(m_copy_number != 0) { m_Solids.push_back( m_Solids[0] ); }
00254     m_translation.push_back( pos );
00255     m_rotation.push_back( RotationFromXaxis );
00256     
00257     // Define logical volumes of reflector around target tube
00258     m_cladLogicalVolumes.push_back( new G4LogicalVolume(m_cladSolidVolumes[1], m_ReflectorMaterial, "l"+m_cladName[index+1]+std::to_string(m_copy_number)));  // Material : stainless steel
00259 //     m_cladLogicalVolumes.push_back( new G4LogicalVolume(m_cladSolidVolumes[1], m_CoatingMaterial, "l"+m_cladName[index+1]+std::to_string(m_copy_number)));  // Material : stainless steel
00260     
00261     if(m_copy_number != 0) { m_Solids.push_back( m_Solids[1] ); }
00262     G4RotationMatrix *rot = new G4RotationMatrix();
00263     if(RotationFromXaxis) {*rot = *RotationFromXaxis;}
00264     rot->rotateY(M_PI/2.*rad);
00265     new G4PVPlacement (rot, *pos, m_cladLogicalVolumes[m_cladLogicalVolumes.size()-1], "p" + m_cladName[index+1] , masterVolume, false, m_copy_number, overlap);
00266     m_translation.push_back( pos );
00267     m_rotation.push_back( rot );
00268 }
