00071 void GeomSVBR100::createGeometry() {
00072     // Cleanup old geometry
00073     G4GeometryManager::GetInstance()->OpenGeometry();
00074     G4PhysicalVolumeStore::GetInstance()->Clean();
00075     G4LogicalVolumeStore::GetInstance()->Clean();
00076     G4SolidStore::GetInstance()->Clean();
00077 
00079     // Placement Panel
00080     bool isVesselPlaced        = false;
00081     bool isTargetPlaced        = false;
00082     bool isCorePlaced          = false;
00083     bool isHeatExchangerPlaced = false;
00084 
00085     if (m_nbTarget >= 1) {
00086         isTargetPlaced = true;
00087     }
00088     if (m_p->int_Option == 0) {
00089         isVesselPlaced        = true;
00090         isCorePlaced          = true;
00091         isHeatExchangerPlaced = true;
00092     }
00093     else if (m_p->int_Option == 1) {
00094         isTargetPlaced = true;
00095     }
00096     else {
00097         isVesselPlaced        = true;
00098         isCorePlaced          = true;
00099         isHeatExchangerPlaced = true;
00100     }
00101 
00102     const G4double offsetAngle_Target = twopi/12.; // Place targets in the heat exchangers location
00104 
00105 
00106     if (m_p->Verbose >= 1) {
00107         G4cout << "---------------- Begin SVBR100 Geometry ------------------" << G4endl;
00108         m_overlap = true;
00109     }
00110 
00112     // World Volume
00113     G4Box* sUniverse = new G4Box("sUniverse", 2.5 * m, 2.5 * m, 3.7 * m);
00114     m_lUniverse = new G4LogicalVolume( sUniverse, matUniverse, "lUniverse");
00115 
00116     m_masterVolume =
00117     new G4PVPlacement( 0,  // no rotation
00118         G4ThreeVector(),   // at (0,0,0)
00119         m_lUniverse,         // its logical volume
00120         "Universe",        // its name
00121         0,                 // its mother volume
00122         false,             // no boolean operation
00123         0,                 // copy number
00124         m_overlap
00125     );
00126 
00128     // Reactor Vessels
00129     m_distance_centerHX_centerCore = 1780.*mm;
00130     if (isVesselPlaced) {
00131         buildVessel();
00132     }
00133 
00135     // Core
00136     const G4double distance_centerCore_topVessel = 5.1 * m; 
00137     const G4double zOffset_fuelPin = (-900. + 8. + 40. + 1550./2. + 150/2. - 65/2.) * mm;
00138     const G4double zPosition_coreCenter = m_zPosition_topReactor - distance_centerCore_topVessel - zOffset_fuelPin;
00139     G4cout << "zPosition_coreCenter : " << zPosition_coreCenter/mm << " mm" << G4endl;
00140     if (isCorePlaced) {
00141         buildCore(
00142             !m_lPrimaryCoolant ? m_lUniverse : m_lPrimaryCoolant,
00143             Pb_Bi,
00144             !m_lPrimaryCoolant ? G4ThreeVector() : G4ThreeVector(0., 0., zPosition_coreCenter),
00145             offsetAngle_Target
00146         );
00147     }
00148 
00150     // Target
00151     const G4double distance_beamWindow_coreCenter = 14.1 * cm;
00152     m_zPosition_beamWindow = zPosition_coreCenter + distance_beamWindow_coreCenter + zOffset_fuelPin;
00153     //G4cout << "Position of the beam window: " << m_zPosition_beamWindow << G4endl;
00154     const G4double extraLength_lower = 1200. * mm; // distance_centerCore_topVessel + 625.*mm - distance_beamWindow_coreCenter - 3050.*mm;
00155     const G4double extraLength_upper = distance_centerCore_topVessel + 625.*mm - distance_beamWindow_coreCenter - 3050.*mm - extraLength_lower*0; // 0.*mm;
00156 
00157     if (isTargetPlaced) {
00158         if (!m_isMultipleExternalTarget) {
00159             buildTarget(
00160                 !m_lPrimaryCoolant ? m_lUniverse     : m_lPrimaryCoolant,
00161                 !m_lPrimaryCoolant ? G4ThreeVector() : G4ThreeVector(0., 0., m_zPosition_beamWindow), // Target Center == Lower Cylinder
00162                 !m_lPrimaryCoolant ? 0.              : extraLength_lower,
00163                 !m_lPrimaryCoolant ? 0.              : extraLength_upper
00164             );
00165         }
00166         else {
00167             buildMultipleExternalTarget(
00168                 !m_lPrimaryCoolant ? m_lUniverse     : m_lPrimaryCoolant,
00169                 !m_lPrimaryCoolant ? G4ThreeVector() : G4ThreeVector(0., 0., m_zPosition_topReactor - m_halfHeight_primaryCoolantCylinder), // Target Center == Lower Cylinder
00170                 !m_lPrimaryCoolant ? 0.              : extraLength_lower * 0,
00171                 !m_lPrimaryCoolant ? 0.              : extraLength_upper,
00172                 offsetAngle_Target
00173             );
00174         }
00175     }
00176 
00178     // Heat Exchangers
00179     //const G4double zPosition_heatExchanger = m_zPosition_topReactor - m_halfHeight_primaryCoolantCylinder;
00180     if (isHeatExchangerPlaced) {
00181         if (!m_isMultipleExternalTarget) {
00182             buildHeatExchangers(
00183                 !m_lPrimaryCoolant ? m_lUniverse : m_lServiceModule,
00184                 Pb_Bi,
00185                 G4ThreeVector()
00186             );
00187         }
00188         else {
00189 
00190         }
00191     }
00192 
00193     if (m_p->Verbose >= 1) {
00194         G4cout << "----------------- End SVBR100 Geometry -------------------" << G4endl;
00195     }
00196 
00197     if (m_p->Verbose >= 2) {
00198         G4cout << *(G4Material::GetMaterialTable()) << G4endl;
00199     }
00200 
00201     if (m_p->boolVisualization) {
00202         VisualisationConfig();
00203     }
00204 }
