00206 void GeomSVBR100::buildVessel() {
00209     const G4double thickness_vessel = 50. * mm; // 5 cm Stainless Steel 316L
00210     const G4double thickness_vesselGap = 10. * cm; // Gap between the inner and outer vessel, fill with Argon
00211     // Safety Vessel
00212     const G4double outerRadius_safetyVessel = 4.53/2. * m; // 2.19 * m; -> 2.265 * m
00213     const G4double innerRadius_safetyVessel = outerRadius_safetyVessel - thickness_vessel;
00214     // Primary Vessel
00215     const G4double outerRadius_primaryVessel = innerRadius_safetyVessel - thickness_vesselGap;
00216     m_innerRadius_primaryVessel = outerRadius_primaryVessel - thickness_vessel;
00217     // Height
00218     const G4double totalHeight_safetyVessel = 6.920 * m; // 10.290 * m; -> 6.920 * m
00219     const G4double totalHeight_vesselGap = totalHeight_safetyVessel - thickness_vessel/2.;
00220     const G4double totalHeight_primaryVessel = totalHeight_vesselGap - thickness_vesselGap/2.;
00221     const G4double totalHeight_primaryCoolant = totalHeight_primaryVessel - thickness_vessel/2.;
00222 
00223     if (m_p->Verbose >= 2) {
00224         G4cout << "Total Height Safety Vessel : " << totalHeight_safetyVessel << " m" << G4endl
00225                << "Diameter Safety Vessel : " << 2. * outerRadius_safetyVessel << " m" << G4endl
00226                << "Total Height Primary Vessel : " << totalHeight_primaryVessel << " m" << G4endl
00227                << "Diameter Primary Vessel : " << 2. * outerRadius_primaryVessel << " m" << G4endl;
00228     }
00229 
00230     // Vessel Top
00231     const G4double halfThickness_topVessel = 200./2. * mm;
00232     const G4double outerRadius_topVessel = outerRadius_safetyVessel + 100. * mm;
00233 
00234     // Ellipsoid factor
00235     const G4double ellipsoidFactor = 0.5;
00236 
00237     // Position center to the Universe
00238     const G4double zPosition_reactor = outerRadius_safetyVessel*ellipsoidFactor - totalHeight_safetyVessel/2.;
00239     G4cout << "Relative Position Reactor: " << zPosition_reactor/mm << " mm" << G4endl;
00240     const G4double zPosition_vesselTop = totalHeight_safetyVessel/2. + halfThickness_topVessel;
00241     m_zPosition_topReactor = totalHeight_safetyVessel - outerRadius_safetyVessel * ellipsoidFactor;
00242 
00243     m_halfHeight_primaryCoolantCylinder = (totalHeight_primaryCoolant - ellipsoidFactor*m_innerRadius_primaryVessel) * 0.5;
00244 
00245 
00246     //--------------------------------------------------------------------------*
00247 
00249     // Vessel Top
00250     G4Tubs* sVesselTop = new G4Tubs("sVesselTop", 0., outerRadius_topVessel, halfThickness_topVessel, 0., twopi);
00251     G4LogicalVolume* lVesselTop = new G4LogicalVolume(sVesselTop, SS316L, "lVesselTop");
00252     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_vesselTop), lVesselTop, "pVesselTop", m_lUniverse, false, 0, m_overlap);
00253 
00254 
00256     // Safety Vessel
00257     G4LogicalVolume* lSafetyVessel = new G4LogicalVolume(
00258         getContainerLogicalVolume("sSafetyVessel", totalHeight_safetyVessel, outerRadius_safetyVessel, ellipsoidFactor),
00259         SS316L,
00260         "lSafetyVessel"
00261     );
00262     new G4PVPlacement(0, G4ThreeVector(0., 0., zPosition_reactor), lSafetyVessel, "pSafetyVessel", m_lUniverse, false, 0, m_overlap);
00263 
00265     // Reactor Gap between Safety and Primary Vessel
00266     G4LogicalVolume* lVesselGap = new G4LogicalVolume(
00267         getContainerLogicalVolume("sVesselGap", totalHeight_vesselGap, innerRadius_safetyVessel, ellipsoidFactor),
00268         matUniverse,
00269         "lVesselGap"
00270     );
00271     new G4PVPlacement(0, G4ThreeVector(), lVesselGap, "pVesselGap", lSafetyVessel, false, 0, m_overlap);
00272 
00274     // Reactor Primary Vessel
00275     G4LogicalVolume* lPrimaryVessel = new G4LogicalVolume(
00276         getContainerLogicalVolume("sPrimaryVessel", totalHeight_primaryVessel, outerRadius_primaryVessel, ellipsoidFactor),
00277         SS316L,
00278         "lPrimaryVessel"
00279     );
00280     new G4PVPlacement(0, G4ThreeVector(), lPrimaryVessel, "pPrimaryVessel", lVesselGap, false, 0, m_overlap);
00281 
00283     // Primary Coolant
00284     m_lPrimaryCoolant = new G4LogicalVolume(
00285         getContainerLogicalVolume("sPrimaryCoolant", totalHeight_primaryCoolant, m_innerRadius_primaryVessel, ellipsoidFactor),
00286         Pb_Bi,
00287         "lPrimaryCoolant"
00288     );
00289     new G4PVPlacement(0, G4ThreeVector(), m_lPrimaryCoolant, "pPrimaryCoolant", lPrimaryVessel, false, 0, m_overlap);
00290 
00292     // Service Module
00293     if (!m_isMultipleExternalTarget) {
00294         const G4double innerRadius_serviceModule = 2. * m_distance_centerHX_centerCore - m_innerRadius_primaryVessel; // Neutron Shield
00295         G4Tubs* sServiceModule = new G4Tubs("sServiceModule", innerRadius_serviceModule, m_innerRadius_primaryVessel, m_halfHeight_primaryCoolantCylinder, 0., twopi);
00296         m_lServiceModule = new G4LogicalVolume(sServiceModule, Pb_Bi, "m_lServiceModule");
00297         new G4PVPlacement(0, G4ThreeVector(0., 0., m_zPosition_topReactor - m_halfHeight_primaryCoolantCylinder), m_lServiceModule, "pServiceModule", m_lPrimaryCoolant, false, 0, m_overlap);
00298     }
00299 }
