00301 void GeomSVBR100::buildTarget(G4LogicalVolume* motherVolume, G4ThreeVector translation, G4double extraLength_lower, G4double extraLength_upper) {
00302     TMXTarget* target = new TMXTarget("Target", m_p, motherVolume);
00303     // target->setEnvelopeMaterial(SS316L);
00304     target->createGeometry(extraLength_lower, extraLength_upper);
00305     target->placeGeometry(translation);
00306 
00307     m_targetVector.push_back(target);
00308 }
