00310 void GeomSVBR100::buildMultipleExternalTarget(G4LogicalVolume* motherVolume, G4ThreeVector translation, G4double extraLength_lower, G4double extraLength_upper, G4double offsetAngle_Target) {
00311     G4double x, y;
00312     std::string ext;
00313     G4Tubs* sTargetContainer = new G4Tubs("sTargetContainer", 0., 455./2. * mm, m_halfHeight_primaryCoolantCylinder, 0., twopi);
00314     G4cout << "Target Positons :" << G4endl;
00315     for (int i = 0; i < m_nbTarget; i++) {
00316         ext = "_"+std::to_string(i);
00317         x = m_distance_centerHX_centerCore * cos((double)i/(double)m_nbTarget * twopi + offsetAngle_Target);
00318         y = m_distance_centerHX_centerCore * sin((double)i/(double)m_nbTarget * twopi + offsetAngle_Target);
00319 
00320         G4cout << "* Target " << i << " : (x: " << x << ", y: " << y << ")" << G4endl;
00321 
00322         G4LogicalVolume* lTargetContainer = new G4LogicalVolume(sTargetContainer, Pb_Bi, "lTargetContainer"+ext);
00323         new G4PVPlacement(0, G4ThreeVector(x, y, 0.) + translation, lTargetContainer, "pTargetContainer"+ext, motherVolume, false, 0, m_overlap);
00324 
00325         TMXTarget* target = new TMXTarget("target"+ext, m_p, lTargetContainer);
00326         // target->setEnvelopeMaterial(SS316L);
00327         target->createGeometry(extraLength_lower, extraLength_upper);
00328         target->placeGeometry(G4ThreeVector(0., 0., m_zPosition_beamWindow-m_halfHeight_primaryCoolantCylinder));
00329 
00330         m_targetVector.push_back(target);
00331     }
00332 }
