00334 void GeomSVBR100::buildCore(G4LogicalVolume* motherVolume, TMXMaterial* motherMaterial, G4ThreeVector translation, G4double offsetAngle_Target) {
00335     m_core = new SVBR100_Core(m_p, motherVolume, m_isMultipleExternalTarget, m_nbTarget, m_distance_centerHX_centerCore, offsetAngle_Target);
00336     m_core->setPrimaryCoolantMaterial(motherMaterial);
00337     m_core->setMetalicStructureMaterial(SS316L);
00338     // m_core->setInnerRadiusActiveCore(m_target->getOuterRadiusUpperBeamEnvelope());
00339     if (m_isBurnupDivision) {
00340         m_core->setBurnupDivision(true, 5); // (isRoundAssemblyDivision, nbAxialFuelPinDivision)
00341     }
00342 
00343     m_core->createGeometry();
00344     m_core->placeGeometry(translation);
00345 }
