00347 void GeomSVBR100::buildHeatExchangers(G4LogicalVolume* motherVolume, TMXMaterial* motherMaterial, G4ThreeVector translation) {
00348     /*///////////////////////////////
00349     // Service Module
00350     const G4double innerRadius_serviceModule = m_core->getOuterRadiusNeutronShield();
00351     G4VSolid* sServiceModule = new G4Tubs("sServiceModule", innerRadius_serviceModule, m_innerRadius_primaryVessel, m_halfHeight_primaryCoolantCylinder, 0., twopi);
00352     m_lServiceModule = new G4LogicalVolume(sServiceModule, motherMaterial, "m_lServiceModule");
00353     new G4PVPlacement(0, translation, m_lServiceModule, "pServiceModule", motherVolume, false, 0, m_overlap);*/
00354 
00356     // Heat Exchangers
00357     G4double halfLength_insideReactor = 5150./2. * mm;
00358     m_heatEchanger = new SVBR100_HeatExchanger(m_p, motherVolume);
00359     m_heatEchanger->setPrimaryCoolantMaterial(motherMaterial);
00360     m_heatEchanger->setMetalicStructureMaterial(SS316L);
00361     m_heatEchanger->createGeometry(halfLength_insideReactor);
00362 
00363     G4double x, y, z = m_halfHeight_primaryCoolantCylinder - halfLength_insideReactor;
00364     const int nSectors = 14;
00365     for (int i=0; i<nSectors; i++) {
00366         if ((2*i) % nSectors != 0) { // Remove two HX for the EM Pomps
00367             x = m_distance_centerHX_centerCore * cos((double)i/(double)nSectors * twopi);
00368             y = m_distance_centerHX_centerCore * sin((double)i/(double)nSectors * twopi);
00369             m_heatEchanger->placeGeometry(G4ThreeVector(x, y, z) + translation);
00370         }
00371     }
00372 }
