00457 G4VSolid* GeomSVBR100::getContainerLogicalVolume(std::string name, G4double fullHeight, G4double radius, double ellipsoidFactor) {
00458     G4double halfHeight_cylinder = (fullHeight - radius * ellipsoidFactor) * 0.5;
00459 
00460     G4VSolid* sContainer_cylinder = new G4Tubs(name+"_cylinder", 0., radius, halfHeight_cylinder, 0., twopi);
00461     G4Ellipsoid* sContainer_ellipsoid = new G4Ellipsoid(name+"_ellipsoid", radius, radius, radius * ellipsoidFactor, -radius, 0.);
00462     G4VSolid* sContainer = new G4UnionSolid(name, sContainer_ellipsoid, sContainer_cylinder, 0, G4ThreeVector(0., 0., halfHeight_cylinder));
00463     return(sContainer);
00464 }
