00469 void GeomSVBR100::VisualisationConfig() {
00470     if(m_p->Verbose >= 1) {
00471         std::cout << "\nConfiguration of the visualisation" << std::endl;
00472     }
00473 
00475     // visualisation colors configured by DL
00477     G4Colour black   ( .0   , .0    , .0         ); // black
00478     G4Colour lblack  ( .0   , .0    , .0    , 0.6); // light black
00479     G4Colour vlblack ( .0   , .0    , .0    , 0.2); // very light black
