00101 G4VPhysicalVolume* GeomKIPT::Construct()
00102 {
00103     // Cleanup old geometry
00104     G4GeometryManager::GetInstance()->OpenGeometry();
00105     G4PhysicalVolumeStore::GetInstance()->Clean();
00106     G4LogicalVolumeStore::GetInstance()->Clean();
00107     G4SolidStore::GetInstance()->Clean();
00108 
00109     // Material definition
00110     DefineMaterials();
00111 
00112     // To check volume overlapping (=true)
00113     overlap = false;
00114     if (p->Verbose == 1)
00115         overlap = true;
00116 
00117 //--------------------------------------------------------------------------------------------------------------------------
00118 
00119     if(p->Verbose == 1)
00120     {
00121         printf("=============================== KIPT Geometry Construction ==================================\n");
00122     }
00123 
00124 
00125     G4Box* sWorld= new G4Box("sWorld", 1. * m , 1. * m , 2. * m  );
00126     fLogicWorld1= new G4LogicalVolume( sWorld, matWorld, "fLogicWorld1" );
00127 
00129     fWorld1 =
00130     new G4PVPlacement( 0,          // no rotation
00131                 G4ThreeVector(),   // at (0,0,0)
00132                 fLogicWorld1,            // its logical volume
00133                 "Universe",        // its name
00134                 0,                 // its mother volume
00135                 false,             // no boolean operation
00136                 0,                 // copy number
00137                 overlap);
00138 
00139 
00140 
00141     if (p->Verbose == 1) { std::cout << "Make Reflector Block" << std::endl; }
00142     MakeReflectorBlock();
00143 
00144     if (p->Verbose == 1) { std::cout << "Make Reactor Core" << std::endl; }
00145     MakeReactorCore();
00146 
00147     if (p->Verbose == 1) { std::cout << "Make Neutron Source" << std::endl; }
00148     MakeNeutronSource();
00149 
00150 //    if (p->Verbose == 1) { std::cout << "Make Subtractions" << std::endl; }
00151 //    MakeSubtractions();
00152 
00153     VisualisationConfig();
00154 
00155     if(p->Verbose == 1)
00156     G4cout << *(G4Material::GetMaterialTable()) << G4endl;
00157 
00158     return fWorld1;
00159 }
