00174 void GeomKIPT::MakeNeutronSource() {  // Build the KIPT target with the beam pipe
00175     // Target definition
00176     m_TMXNeutronSource = new TMXNeutronSource(p);
00177 
00178     G4ThreeVector targetLocation = G4ThreeVector(0., 0., 0.);
00179 /*    for(int i=0; i<p->SourceNumber; ++i) {
00180         G4ThreeVector Pos (p->src_XYZposition [i][0],p->src_XYZposition [i][1],p->src_XYZposition [i][2]);
00181         G4ThreeVector beam(p->src_XYZdirection[i][0],p->src_XYZdirection[i][1],p->src_XYZdirection[i][2]);
00182         m_TMXNeutronSource->placeGeometry(overlap, &Pos, &beam, fCore);
00183     }*/
00184     TMXTargetKIPT2* neutEmmiter = (TMXTargetKIPT2*)( m_TMXNeutronSource->getNeutronEmmiter());
00185     new G4PVPlacement(0, targetLocation, neutEmmiter->getMasterVolume(), "pTarget",  m_TMXCore->getMasterVolume(), 0, 0, true);
00186     TMXBeamPipeKIPT2* beamPipe = (TMXBeamPipeKIPT2*) (m_TMXNeutronSource->getBeamPipe());
00187     new G4PVPlacement(0, G4ThreeVector(0., 0.,  (50. / 4. - 5.4 / 4. + 5.4 / 2.) * cm  + targetLocation.z() ), beamPipe->getMasterVolume(), "pBeamPipe",  m_TMXCore->getMasterVolume(), 0, 0, true);
00188 }
