00190 void GeomKIPT::MakeReactorCore() {
00191     // File that contain the configuration of the core
00192     std::string latticeMapFile = "../../ADS_SimulationCode/ADS_SimulationCode/G4-NeutronTransport/src/latticeMap/LatticeKIPT2_W.txt";
00193 
00194     m_TMXCore = new TMXCoreKIPT("Core", p, latticeMapFile, m_TMXNeutronSource);
00195     fCore = m_TMXCore->getMasterVolume();
00196 
00197     m_TMXCore->AddAssembliesInCore();
00198 
00199     m_TMXCore->placeGeometry(overlap, G4ThreeVector(), 0, m_TMXReflectorBlock->getMasterVolume(), "hexagonal");
00200 }
