00206 void GeomKIPT::MakeReflectorBlock() {
00207     m_TMXReflectorBlock = new TMXReflectorKIPT("ReflectorBlock", p);
00208     fReflectorBlock = m_TMXReflectorBlock->getMasterVolume();
00209     G4RotationMatrix* zRot = new G4RotationMatrix; // Rotates X and Z axes only
00210     zRot->rotateZ(0.*deg);
00211     m_TMXReflectorBlock->placeGeometry(overlap, G4ThreeVector(), zRot, fLogicWorld1);
00212 }
