00256 void GeomKIPT::DefineMaterials() {
00257     /* Core Material Characteristic from Source Doc: THPF080
00258     Reflectors:
00259         - Intrinsic zone: Beryllium
00260         - Outside zone: Graphite
00261     Moderator/Coolant: Demineralized Water (H2O)
00262     Fuel: Dioxyde Uranium with 19.7% U235 (Fuel type WWR-M2 by TVEL corporation)
00263         uniformely distributed in an aluminium matrix
00264     */
00266     // Demineralised Water - Coolant
00267     TMXMaterial* _DemineralizedWater = new TMXMaterial("Demineralised Water", 1.0*(g/cm3), 2, kStateLiquid, 100); // !!! Temperature
00268     _DemineralizedWater->AddElement(80160, 1, "O_16");
00269     _DemineralizedWater->AddElement(10010, 2, "H_1");
00270 
00271     matWorld = _DemineralizedWater;
00272 }
