00045 TMXTargetKIPT::TMXTargetKIPT(TMXInputParameters* parameters, const std::string& TargetAssemblyName):
00046 TMXAbstractTargetAssembly(parameters, TargetAssemblyName)
00047 {
00048     createMaterial(m_p->TemperatureWorld);
00049     createGeometry();
00050 }
00051 
00052 TMXTargetKIPT::~TMXTargetKIPT() {}
00053 
00054 void TMXTargetKIPT::createMaterial(double Kelvin_temperature) {
00055     
00056     // Default Materials
00057     TMXAbstractTargetAssembly::createMaterial(Kelvin_temperature);
00058     
00059     // Material of coating
00060     G4Material* g4_Ta = G4NistManager::Instance()->FindOrBuildMaterial("G4_Ta");
00061     m_CoatingMaterial = new G4Material(m_assemblyName + "_Tantalum",g4_Ta->GetDensity(), 1, kStateSolid, Kelvin_temperature);
00062     m_CoatingMaterial->AddMaterial(g4_Ta,1);
00063 }
00064 
00065 void TMXTargetKIPT::createGeometry() {  // This method should be called in the constructor after calling createMaterial
00066 //     std::cout << "Beginning of Target definition" << std::endl;
00067     
00068     // TODO get these values from parameter file
00069     //   std::vector<double> vectorTargetThickness = {3., 3., 3., 4., 4., 6., 10.}; // Thickness of target slabs in KIPT
00070     m_vectorTargetThickness = m_p->src_slabThicknesses; // Thickness of target slabs in our experiment
00071     
00073     // Re-definition of target slabs name
00074     std::string slabName;
00075     if( m_targetName.size() > 0) { // Last element is a generic name added by the constructor
00076         slabName = m_targetName[m_targetName.size()-1]; // save generic name
00077         m_targetName.pop_back();} // remove generic name from the vector of names
00078     for(int i=0; i<(int)m_vectorTargetThickness.size(); ++i) { m_targetName.push_back(slabName+ std::to_string(i)); }
00079     for(int i=0; i<(int)m_vectorTargetThickness.size(); ++i) { m_targetName.push_back(m_assemblyName + "_SlabCoating"+ std::to_string(i)); }
00081     
00082     G4double targetPipeHeight = m_p->zTargetHeight + 4 * m_p->CheckGap;// Tube height along Z-axis     // 2 x checkgap = thickness of wall + gap between walls and target slabs
00083     G4double targetPipeWidth  = m_p->yTarget + 4 * m_p->CheckGap;      // Tube width  along Y-axis
00084     G4double targetPipeThickness = m_p->CheckGap;  
00085     
00086     // TODO get this value from parameter file
00087     m_coatingThickness = 0.25*mm;   // W-slabs are coated with tantalum
00088     
00089     // Define solid volumes of slabs
00090     m_targetPipeLength = 2 * m_p->CheckGap + targetPipeThickness; // Tube length : 2 x gap between wall and W-slabs + 1 x wall thickness
00091     for(int i=0; i<(int)m_vectorTargetThickness.size(); ++i) {
00092         m_targetPipeLength += m_vectorTargetThickness[i] + m_p->targetgap + 2 * m_coatingThickness;
00093         m_targetSolidVolumes.push_back(new G4Box("s" + m_targetName[i], 0.5*m_vectorTargetThickness[i], 0.5*m_p->yTarget, 0.5*m_p->zTargetHeight)); // Half size X,Y,Z
00094         m_coatingSolidVolumes.push_back(new G4Box("s"+ m_targetName[i + m_vectorTargetThickness.size()], 0.5*m_vectorTargetThickness[i] + m_coatingThickness, 0.5*m_p->yTarget + m_coatingThickness, 0.5*m_p->zTargetHeight + m_coatingThickness)); // Half size X,Y,Z
00095     }
00096     if(m_vectorTargetThickness.size() > 0) {m_targetPipeLength = m_targetPipeLength - m_p->targetgap;}
00097 
00098     // Define solid volumes ofcoolant
00099     m_coolantSolidVolumes.push_back( new G4Box("s"+m_coolantName[m_coolantName.size()-1], 0.5*(m_targetPipeLength-targetPipeThickness), 0.5*targetPipeWidth-targetPipeThickness, 0.5*targetPipeHeight-targetPipeThickness));  // Half size X,Y,Z
00100     
00101     // Define solid volumes of target tube
00102     m_cladSolidVolumes.push_back( new G4Box("s"+m_cladName[m_cladName.size()-1], 0.5*m_targetPipeLength, 0.5*targetPipeWidth, 0.5*targetPipeHeight));  // Half size X,Y,Z
00103     m_Solids.push_back( m_cladSolidVolumes[0] ); // Largest volume surrounding all the geometry -> to be substracted to the rest of the geometry when placed
00104 
00105 //     std::cout << "End of Target definition" << std::endl;
00106 }
00107 
00108 G4ThreeVector TMXTargetKIPT::placeGeometry(bool overlap, G4ThreeVector* centerTarget, G4ThreeVector* beamDirection, G4LogicalVolume* masterVolume) {
00109     G4RotationMatrix* RotationFromXaxis = TMXNeutronSource::DefineRotationMatrix(beamDirection); // Definition of rotation matrix from beamDirection
00110     placeTube(overlap, centerTarget, RotationFromXaxis, masterVolume);        // Placement of tube in the masterVolume (world ?)
00111     placeCoolant(overlap, nullptr, nullptr, m_cladLogicalVolumes[m_cladLogicalVolumes.size()-1]);         // Placement of coolant in tube
00112     placeSlabs  (overlap, nullptr, nullptr, m_coolantLogicalVolumes[m_coolantLogicalVolumes.size()-1]);      // Placement of slabs in coolant
00113     ++m_copy_number;
00114     
00115     G4double targetPipeThickness = m_p->CheckGap;  
00116     G4ThreeVector distanceToOut{-0.5*m_targetPipeLength + m_p->XtargetPosition + 0.5*targetPipeThickness,0,0};
00117     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00118     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00119     if (RotationFromXaxisInverse) { distanceToOut = *RotationFromXaxisInverse * distanceToOut; }  // If rotation, distanceToOut along x axis is rotated
00120     return distanceToOut;
00121 }
00122 
00123 void TMXTargetKIPT::placeSlabs(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00124     size_t index = getFirstOccurence(&m_targetName);
00125     G4double targetPipeThickness = m_p->CheckGap;  
00126     for(int i=0; i<(int)m_vectorTargetThickness.size(); ++i) {    // Target slabs placement + coating
00127         // Definition of target slab + coating logical volumes
00128         m_targetLogicalVolumes .push_back( new G4LogicalVolume(m_targetSolidVolumes [i], m_TargetMaterial,  "l"+m_targetName[ index + i ]+std::to_string(m_copy_number) )); // Material : tungsten        
00129         m_coatingLogicalVolumes.push_back( new G4LogicalVolume(m_coatingSolidVolumes[i], m_CoatingMaterial, "l"+m_targetName[ index + i + m_vectorTargetThickness.size()]+std::to_string(m_copy_number) )); // Material : tantalum
00130         
00131         double Xpos = 0;
00132         for(int j=1; j<=i; ++j) { Xpos += 0.5 * (m_vectorTargetThickness[j] + m_vectorTargetThickness[j-1]) + 2*m_coatingThickness + m_p->targetgap;}
00133         G4ThreeVector pos(-0.5*(m_targetPipeLength-targetPipeThickness) + 0.5*m_vectorTargetThickness[0] + m_coatingThickness + m_p->CheckGap + Xpos,0 ,0);
00134         G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00135         if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00136         if (RotationFromXaxisInverse) { pos = *RotationFromXaxisInverse * pos; }  // If rotation, distanceToOut along x axis is rotated
00137 //         if (RotationFromXaxis) { pos = *RotationFromXaxis * pos; }  // If rotation, distanceToOut along x axis is rotated
00138         if (centerTarget) {pos = *centerTarget + pos;}
00139         new G4PVPlacement (RotationFromXaxis, 
00140                            pos,
00141                            m_coatingLogicalVolumes[m_coatingLogicalVolumes.size()-1], "p" + m_targetName[index + i + m_vectorTargetThickness.size()], masterVolume, false, m_copy_number, overlap);
00142         
00143         new G4PVPlacement (0, G4ThreeVector(0,0,0), m_targetLogicalVolumes[m_targetLogicalVolumes.size()-1], "p" + m_targetName[index + i], m_coatingLogicalVolumes[m_coatingLogicalVolumes.size()-1], false, m_copy_number, overlap);
00144     }
00145 }
00146 
00147 void TMXTargetKIPT::placeCoolant(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00148     size_t index = getFirstOccurence(&m_coolantName);
00149 
00150     // Define logical volumes ofcoolant
00151     m_coolantLogicalVolumes.push_back( new G4LogicalVolume(m_coolantSolidVolumes[0], m_CoolantMaterial, "l"+m_coolantName[index]+std::to_string(m_copy_number)));   // Material : water
00152     
00153     // Place the volume
00154     G4double targetPipeThickness = m_p->CheckGap; 
00155     G4ThreeVector pos(-0.5*targetPipeThickness,0,0);
00156     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00157     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00158     if (RotationFromXaxisInverse) { pos = *RotationFromXaxisInverse * pos; }  // If rotation, distanceToOut along x axis is rotated
00159 //     if (RotationFromXaxis) { pos = *RotationFromXaxis * pos; }  // If rotation, distanceToOut along x axis is rotated
00160     if (centerTarget) {pos = *centerTarget + pos;}
00161     new G4PVPlacement (RotationFromXaxis, pos, m_coolantLogicalVolumes[0], "p" + m_coolantName[index] , masterVolume, false, m_copy_number, overlap);
00162 }
00163 
00164 void TMXTargetKIPT::placeTube(bool overlap, G4ThreeVector* centerTarget, G4RotationMatrix *RotationFromXaxis, G4LogicalVolume* masterVolume) {
00165     size_t index = getFirstOccurence(&m_cladName);
00166 
00167     // Define logical volumes of target tube
00168     m_cladLogicalVolumes.push_back( new G4LogicalVolume(m_cladSolidVolumes[0], m_CladMaterial, "l"+m_cladName[index]+std::to_string(m_copy_number)));  // Material : stainless steel
00169 
00170     // Place the volume
00171     G4double targetPipeThickness = m_p->CheckGap;  
00172     G4ThreeVector* pos = new G4ThreeVector(m_p->XtargetPosition+0.5*targetPipeThickness,0,0);
00173     G4RotationMatrix* RotationFromXaxisInverse = nullptr;
00174     if (RotationFromXaxis) { RotationFromXaxisInverse = new G4RotationMatrix( RotationFromXaxis->axis(), -RotationFromXaxis->delta() );}
00175     if (RotationFromXaxisInverse) { *pos = *RotationFromXaxisInverse * *pos; }  // If rotation, distanceToOut along x axis is rotated
00176 //     if (RotationFromXaxis) { pos = *RotationFromXaxis * pos; }  // If rotation, distanceToOut along x axis is rotated
00177     if (centerTarget) {*pos = *centerTarget + *pos;}
00178     new G4PVPlacement (RotationFromXaxis, *pos, m_cladLogicalVolumes[0], "p" + m_cladName[index] , masterVolume, false, m_copy_number, overlap);
00179     
00180     if(m_Solids.size() > 0 && m_translation.size() > 0 && m_rotation.size() > 0) { m_Solids.push_back( m_Solids[0] ); }
00181     m_translation.push_back( pos );
00182     m_rotation.push_back( RotationFromXaxis );
00183 }
