00105    fEmPhysicsList(0), fParticleList(0)
00106 //    fMessenger(0)
00107 {
00108 
00109   G4LossTableManager::Instance();
00110 
00111   // defaultCutValue  = 0.7*mm;
00112   defaultCutValue  = 0.7e10*km;
00113 
