00119 
00120 //   fMessenger = new PhysicsListMessenger(this);
00121 
00122   // Particles
00123 //  fParticleList = new G4DecayPhysics("decays"); //disactivated for activation calculation. Create isomeric states with 0 ps of half life
00124 
00125   // EM physics
00126   fEmPhysicsList = new G4EmStandardPhysics();
