00152 void PhysicsList::ConstructProcess()
00153 {
00154   AddTransportation();
00155 
00156   ConstructGeneral(); // Added by D. Maire on November 4th, 2019
00157 
00158   // fEmPhysicsList->ConstructProcess();
00159 
00160   fParticleList->ConstructProcess();
00161 
00162   for( size_t i = 0; i < fHadronPhys.size(); i++) 
00163   {
00164     fHadronPhys[i]->ConstructProcess();
00165   }
00166   
00167 }
