00170 
00171 // Added by D. Maire on November 4th, 2019
00172 #include "G4ParallelWorldScoringProcess.hh"
00173 void PhysicsList::ConstructGeneral()
00174 {
00175   // Add Parallel world Scoring Process
00176   G4ParallelWorldScoringProcess* ParallelWorldScoringProcess
00177       = new G4ParallelWorldScoringProcess("ParaWorldScoringProc");
00178   ParallelWorldScoringProcess->SetParallelWorld("ParallelScoringWorld");
00179 
00180   G4ParticleTable::G4PTblDicIterator* theParticleIterator = G4ParticleTable::GetParticleTable()->GetIterator();  
00181   theParticleIterator->reset();
00182   while( (*theParticleIterator)() ){
00183     G4ParticleDefinition* particle = theParticleIterator->value();
00184     G4ProcessManager* pmanager = particle->GetProcessManager();
00185     pmanager->AddProcess(ParallelWorldScoringProcess);
00186     pmanager->SetProcessOrderingToLast(ParallelWorldScoringProcess, idxAtRest);
00187     pmanager->SetProcessOrdering(ParallelWorldScoringProcess, idxAlongStep, 1);
00188     pmanager->SetProcessOrderingToLast(ParallelWorldScoringProcess, idxPostStep);
00189   }
00190 }
00191 // Stop
