00195 void PhysicsList::AddPhysicsList(const G4String& name)
00196 {
00197   if ( verboseLevel > 0 ) 
00198   {
00199     G4cout << "PhysicsList::AddPhysicsList: <" << name << ">" << G4endl;
00200   }
00201   if ( name == "emstandard_opt0" ) 
00202   {
00203     delete fEmPhysicsList;
00204     fEmPhysicsList = new G4EmStandardPhysics();
00205   } 
00206   else if ( name == "emstandard_opt1" ) 
00207   {
00208     delete fEmPhysicsList;
00209     fEmPhysicsList = new G4EmStandardPhysics_option1();
00210   } 
00211   else if (name == "emstandard_opt2") 
00212   {
00213     delete fEmPhysicsList;
00214     fEmPhysicsList = new G4EmStandardPhysics_option2();
00215   } 
00216   else if (name == "emstandard_opt3") 
00217   {
00218     delete fEmPhysicsList;
00219     fEmPhysicsList = new G4EmStandardPhysics_option3();
00220   } 
00221   else if (name == "emstandard_opt4") 
00222   {
00223     delete fEmPhysicsList;
00224     fEmPhysicsList = new G4EmStandardPhysics_option4();
00225   } 
00226   else if (name == "FTFP_BERT_EMV") 
00227   {
00228     AddPhysicsList("emstandard_opt1");
00229     AddPhysicsList("FTFP_BERT");
00230   } 
00231   else if (name == "FTFP_BERT_EMX") 
00232   {
00233     AddPhysicsList("emstandard_opt2");
00234     AddPhysicsList("FTFP_BERT");
00235   } 
00236   else if (name == "FTFP_BERT_EMZ") 
00237   {
00238     AddPhysicsList("emstandard_opt4");
00239     AddPhysicsList("FTFP_BERT");
00240   } 
00241   else if (name == "FTFP_BERT") 
00242   {
00243     // SetBuilderList1();
00244     fHadronPhys.push_back( new G4HadronPhysicsFTFP_BERT() );
00245   } 
00246   else if (name == "FTFPlowBERT") 
00247   {
00248     // SetBuilderList1();
00249     // fHadronPhys.push_back( new G4HadronInFTFPlowBERT() );
00250   } 
00251   else if (name == "FTFP_BERT_TRV") 
00252   {
00253     SetBuilderList3();
00254     fHadronPhys.push_back( new G4HadronPhysicsFTFP_BERT());
00255   } 
00256   else if (name == "FTF_BIC") 
00257   {
00258     //    SetBuilderList0();
00259     fHadronPhys.push_back( new G4HadronPhysicsFTF_BIC());
00260     fHadronPhys.push_back( new G4NeutronCrossSectionXS(verboseLevel));
00261   } 
00262   else if (name == "QBBC") 
00263   {
00264     // AddPhysicsList("emstandard_opt0");
00265     SetBuilderList2();
00266     fHadronPhys.push_back( new G4HadronInelasticQBBC());
00267   } 
00268   else if (name == "QGSP_BERT") 
00269   {
00270     SetBuilderList1();
00271     fHadronPhys.push_back( new G4HadronPhysicsQGSP_BERT());
00272   } 
00273   else if (name == "QGSP_FTFP_BERT") 
00274   {
00275     SetBuilderList1();
00276     fHadronPhys.push_back( new G4HadronPhysicsQGSP_FTFP_BERT());
00277   } 
00278   else if (name == "QGSP_FTFP_BERT_EMV") 
00279   {
00280     AddPhysicsList("emstandard_opt1");
00281     AddPhysicsList("QGSP_FTFP_BERT");
00282   } 
00283   else if (name == "QGSP_BERT_EMV") 
00284   {
00285     AddPhysicsList("emstandard_opt1");
00286     AddPhysicsList("QGSP_BERT");
00287   } 
00288   else if (name == "QGSP_BERT_EMX") 
00289   {
00290     AddPhysicsList("emstandard_opt2");
00291     AddPhysicsList("QGSP_BERT");
00292   } 
00293   else if (name == "QGSP_BERT_HP") 
00294   {
00295     SetBuilderList1(true);
00296     fHadronPhys.push_back( new G4HadronPhysicsQGSP_BERT_HP());
00297   } 
00298   else if (name == "QGSP_BIC") 
00299   {
00300     SetBuilderList0();
00301     fHadronPhys.push_back( new G4HadronPhysicsQGSP_BIC());
00302   } 
00303   else if (name == "QGSP_BIC_EMY") 
00304   {
00305     AddPhysicsList("emstandard_opt3");
00306     SetBuilderList0();
00307     fHadronPhys.push_back( new G4HadronPhysicsQGSP_BIC());
00308   } 
00309   else if (name == "QGS_BIC") 
00310   {
00311     SetBuilderList0();
00312     fHadronPhys.push_back( new G4HadronPhysicsQGS_BIC());
00313     fHadronPhys.push_back( new G4NeutronCrossSectionXS(verboseLevel));
00314   } 
00315   else if (name == "QGSP_BIC_HP") 
00316   {
00317     // AddPhysicsList("emstandard_opt0");
00318     SetBuilderList0(true);
00319     fHadronPhys.push_back( new G4HadronPhysicsQGSP_BIC_HP());
00320   } 
00321   else if (name == "QGSP_INCLXX_HP") 
00322   {
00323     // AddPhysicsList("emstandard_opt0");
00324     SetBuilderList0(true);
00325     fHadronPhys.push_back( new G4HadronPhysicsINCLXX("inclxx-hp",true,true,false));
00326   } 
00327   else if (name == "QGSP_INCLXX") 
00328   {
00329     // AddPhysicsList("emstandard_opt0");
00330     SetBuilderList0(false);
00331     fHadronPhys.push_back( new G4HadronPhysicsINCLXX("inclxx",true,false,false));
00332   } 
00333   else 
00334   {
00335     G4cout << "PhysicsList::AddPhysicsList: <" << name << ">"
00336            << " is not defined"
00337            << G4endl;
00338   }
00339 }
