00343 void PhysicsList::SetBuilderList0( G4bool flagHP)
00344 {
00345   fHadronPhys.push_back( new G4EmExtraPhysics(verboseLevel));
00346 
00347   if(flagHP) 
00348   {
00349     fHadronPhys.push_back( new G4HadronElasticPhysicsHP(verboseLevel) );
00350   } 
00351   else 
00352   {
00353     fHadronPhys.push_back( new G4HadronElasticPhysics(verboseLevel) );
00354   }
00355   fHadronPhys.push_back( new G4StoppingPhysics(verboseLevel));
00356   fHadronPhys.push_back( new G4IonBinaryCascadePhysics(verboseLevel));
00357   fHadronPhys.push_back( new G4NeutronTrackingCut(verboseLevel));
00358 
00359 // Neutron tracking cut
00360   // RegisterPhysics( new G4NeutronTrackingCut(verboseLevel) );
00361 
00362 }
