00366 void PhysicsList::SetBuilderList1( G4bool flagHP)
00367 {
00368   fHadronPhys.push_back( new G4EmExtraPhysics(verboseLevel));
00369 
00370   if(flagHP) 
00371   {
00372     fHadronPhys.push_back( new G4HadronElasticPhysicsHP(verboseLevel) );
00373   } 
00374   else 
00375   {
00376     fHadronPhys.push_back( new G4HadronElasticPhysics(verboseLevel) );
00377 
00378     // activate G4LMsdGenerator:
00379 
00380     // fHadronPhys.push_back( new G4HadronHElasticPhysics(verboseLevel,true) );
00381   }
00382   fHadronPhys.push_back( new G4StoppingPhysics(verboseLevel));
00383   fHadronPhys.push_back( new G4IonPhysics(verboseLevel));
00384   fHadronPhys.push_back( new G4NeutronTrackingCut(verboseLevel));
00385 }
