00447 void PhysicsList::SetCuts()
00448 {
00449 
00450   if ( verboseLevel > 0 )
00451   {
00452     G4cout << "PhysicsList::SetCuts:";
00453 //     G4cout << "CutLength : " << G4BestUnit(defaultCutValue,"Length") << G4endl;
00454   }
00455 
00456   // set cut values for gamma at first and for e- second and next for e+,
00457   // because some processes for e+/e- need cut values for gamma
00458 
00459   SetCutValue(fCutForGamma, "gamma");
00460   SetCutValue(fCutForElectron, "e-");
00461   SetCutValue(fCutForPositron, "e+");
00462   SetCutValue(fCutForProton, "proton");
00463 
00464   if ( verboseLevel > 0 ) { DumpCutValuesTable(); }
00465 }
