00088 void RunAction::BeginOfRunAction(const G4Run* aRun) {
00089 // std::cout << "Begin of BeginRunAction" << std::endl;
00090 
00091     if (m_p->isRandomSeed) {
00092         G4cout << "*** AUTOSEED ON ***" << G4endl;
00093         long seeds[2];
00094         time_t systime = time(NULL);
00095         if(const char* env_p = std::getenv("POD_ID"))
00096             systime = crc32(0, (const Bytef*)&env_p, sizeof(long));
00097         seeds[0] = (long) systime;
00098         seeds[1] = (long) (systime*G4UniformRand());
00099         G4Random::setTheSeeds(seeds);
00100         G4Random::showEngineStatus();
00101     }
00102 
00103   Initialize(fRunNumber, *m_p, fmergedResultsinTextFile);
00104 
00105   // Register ABLA de-excitation model
00106   if (m_p->isAbla) { UseABLAmodel(); } // POSSIBLE ONLY WITH INCL++ in 03/2021. To implement other physics lists if needed !
00107 
00108   // Timer start
00109   fTimer = clock();
00110 
00111   // Print run number and time
00112   PrintRunStart(aRun);
00113 
00114   // Get analysis manager
00115   auto analysisManager = G4AnalysisManager::Instance();
00116 
00117   // Open an output file
00118   analysisManager->OpenFile( std::to_string(fRunNumber) + "_" + m_p->str_ROOTFileName);
00119 
00120 #ifdef G4VIS_USE
00121   G4UImanager* UI = G4UImanager::GetUIpointer();
00122 
00123   G4VVisManager* pVVisManager = G4VVisManager::GetConcreteInstance();
00124 
00125   if(pVVisManager)
00126   {
00127     UI->ApplyCommand("/vis/scene/notifyHandlers");
00128   }
00129 #endif
00130 
00131     // Initialization of G4Run with sensitive detector in a mesh
00132     Run* run = static_cast<Run*>(G4RunManager::GetRunManager()->GetNonConstCurrentRun());
00133     run->Initialize(fRunNumber, m_p->TypeOfMesh);
00134 
00135 // std::cout << "End of BeginRunAction" << std::endl;
00136 }
