00287 
00288 void RunAction::Merge(std::map<TMXMaterial*, std::map<G4int,G4int>>& destinationMap,
00289                 const std::map<TMXMaterial*, std::map<G4int,G4int>>& sourceMap) const
00290 {
00291     for ( const auto& matData : sourceMap )
00292     {
00293         TMXMaterial* mat       = matData.first;
00294         if ( destinationMap.find(mat) == destinationMap.end())
00295             destinationMap[mat] = matData.second;
00296         else
00297         {
00298             for ( const auto& partData : sourceMap.at(mat) )
00299                 destinationMap[mat][partData.first] += partData.second;
00300         }
00301     }
00302 }
