00547 
00548 void RunAction::ReadCartesianMesh(const G4Run* run, int /*Nprotons*/, double /*dbl_Ibeam*/) {
00549     // Mesh cubic volume
00550     G4double CubicVolume = ((m_p->maxX-m_p->minX)/m_p->nbinsX * (m_p->maxY-m_p->minY)/m_p->nbinsY * (m_p->maxZ-m_p->minZ)/m_p->nbinsZ) /cm3;  // Cartesian
00551 
00552     // Pitch of the binning
00553     G4double DeltaX = (m_p->maxX-m_p->minX)/(double)m_p->nbinsX; // X pitch = X box length
00554     G4double DeltaY = (m_p->maxY-m_p->minY)/(double)m_p->nbinsY; // Y pitch = Y box length
00555     G4double DeltaZ = (m_p->maxZ-m_p->minZ)/(double)m_p->nbinsZ; // Z pitch = Z box length
00556 
00557     auto analysisManager = G4AnalysisManager::Instance();
00558     const Run* theRun = (const Run*)run;
00559     G4double Xpos, Ypos, Zpos;
00560 
00561     // For each scored quantity of the sensitive detector
00562     for(int k=0;k<theRun->GetNumberOfSD();k++) {
00563 
00564         const G4THitsMap<G4double> map = theRun->GetFullMap(k); // Get the 3D map of the scoring quantity
00565         std::map<G4int,G4double*>::iterator itr = map.GetMap()->begin();
00566         std::map<G4int,G4double*>::iterator itr_end = map.GetMap()->end();
00567         G4double value = 0.;
00568         G4int key = 0, binX, binY, binZ;
00569 
00570 //         for(; itr != map.GetMap()->end(); ++itr) {
00571         for(; itr != itr_end; ++itr) {
00572             key   = (itr->first);   // key of the mesh
00573             value = *(itr->second);
00574 
00575             // Bin number for each axis
00576             binX = (int)(key/(int)(m_p->nbinsY*m_p->nbinsZ));
00577             binY = (int)(key/(int)(m_p->nbinsZ) - binX * m_p->nbinsY);
00578             binZ = key - binY * m_p->nbinsZ - binX * m_p->nbinsY * m_p->nbinsZ;
00579 
00580            // Position of the bin number
00581             Xpos = (binX + 0.5) * DeltaX + m_p->minX;
00582             Ypos = (binY + 0.5) * DeltaY + m_p->minY;
00583             Zpos = (binZ + 0.5) * DeltaZ + m_p->minZ;
00584 
00585             if(k==0) { // energy deposit for every particles
00586 
00587                 // Fill 2D with the projected mean value
00588                 analysisManager->FillH2( 0, Xpos/mm, Ypos/mm, value/eV  / (CubicVolume * m_p->nbinsZ));
00589                 analysisManager->FillH2(10, Xpos/mm, Zpos/mm, value/eV  / (CubicVolume * m_p->nbinsY));
00590 
00591                // Fill the ntuple to get a 3D mesh
00592                 analysisManager->FillNtupleFColumn( 2, 0,Xpos/mm);              // ID= 0 radial pos in mm
00593                 analysisManager->FillNtupleFColumn( 2, 1,Ypos/mm);              // ID= 1 Theta in rad
00594                 analysisManager->FillNtupleFColumn( 2, 2,Zpos/mm);              // ID= 2 PosZ in mm
00595                 analysisManager->FillNtupleDColumn( 2, 3,value/eV ); // ID= 3 Kinetic energy in eV
00596                 analysisManager->AddNtupleRow(2);
00597             }
00598             if(k==3 || k==4) // Energy deposit for proton or gamma
00599                 analysisManager->FillH2(k, Xpos/mm, Ypos/mm, value/eV  / CubicVolume / m_p->nbinsZ);
00600             else if (k==1) { // neutron flux
00601                 analysisManager->FillH2(1, Xpos/mm, Ypos/mm, value/(1/cm2)  / m_p->nbinsZ);
00602                 analysisManager->FillH2(6, Xpos/mm, Zpos/mm, value/(1/cm2)  / m_p->nbinsY);
00603             }
00604             else if (k==2) { // gamma flux
00605                 analysisManager->FillH2( 2, Xpos/mm, Ypos/mm, value/(1/cm2)  / m_p->nbinsZ);
00606                 analysisManager->FillH2(11, Xpos/mm, Zpos/mm, value/(1/cm2)  / m_p->nbinsY);
00607             }
00608         }
00609     }
00610 }
00611 
00612 void RunAction::ReadPolarMesh(const G4Run* run, int /*Nprotons*/, double /*dbl_Ibeam*/) {
00613 
00614     // Defining Z axis binning
00615     double minZ, maxZ, nbinsZ;
00616     if (m_p->polarAxis == "Y") { // if Y-axis (cartesian) corresponds to the Z-axis in polar coordinates
00617         minZ   = m_p->minY;
00618         maxZ   = m_p->maxY;
00619         nbinsZ = m_p->nbinsY;
00620     }
00621     else if (m_p->polarAxis == "Z") { // if Z-axis (cartesian) corresponds to the Z-axis in polar coordinates
00622         minZ   = m_p->minZ;
00623         maxZ   = m_p->maxZ;
00624         nbinsZ = m_p->nbinsZ;
00625     }
00626     else { // Default is X-axis in cartesian coordinates corresponding to Z-axis in polar coordinates
00627         minZ   = m_p->minX;
00628         maxZ   = m_p->maxX;
00629         nbinsZ = m_p->nbinsX;
00630     }
00631 
00632     // Pitch of the binning
00633     G4double DeltaR = (m_p->maxR-m_p->minR)/(double)m_p->nbinsR; // R pitch = thickness of the tub
00634     G4double DeltaTheta = (m_p->maxTheta-m_p->minTheta)/(double)m_p->nbinsTheta; // Theta pitch = angle of the arc (part of the tub)
00635     G4double DeltaZ = (maxZ-minZ)/(double)nbinsZ; // Z pitch = Height of the tub
00636 
00637     auto analysisManager = G4AnalysisManager::Instance(); // Instantiate the analysis manager
00638     const Run* theRun = (const Run*)run; // Get the run containing the sensitive detector Hits map
00639     G4double Radius, Theta, Zpos;
00640 
00641     // For each scored quantity of the sensitive detector
00642     for(int k=0;k<theRun->GetNumberOfSD();k++) {
00643 
00644         const G4THitsMap<G4double> map = theRun->GetFullMap(k); // Get the 3D map of the scoring quantity
00645         std::map<G4int,G4double*>::iterator itr = map.GetMap()->begin();
00646         G4double value = 0.;
00647         G4int key = 0., binR, binTheta, binZ;
00648 
00649         for(; itr != map.GetMap()->end(); ++itr) {
00650             key   = (itr->first);   // key of the mesh
00651             value = *(itr->second);
00652 
00653             // Bin number for each axis
00654             binR     = (int) (key/(int)(m_p->nbinsTheta*nbinsZ));
00655             binTheta = (int)((key - binR * m_p->nbinsTheta*nbinsZ)/(int)(nbinsZ));
00656             binZ     =  key - binTheta * nbinsZ - binR * m_p->nbinsTheta * nbinsZ;
00657 
00658             // Position of the bin number
00659             Radius = (binR     + 0.5) * DeltaR     + m_p->minR;
00660             Theta  = (binTheta + 0.5) * DeltaTheta + m_p->minTheta;
00661             Zpos   = (binZ     + 0.5) * DeltaZ     + minZ;
00662 
00663             // Mesh cubic volume
00664             G4double CubicVolume = (((m_p->maxTheta-m_p->minTheta)*CLHEP::pi/180. /(double)m_p->nbinsTheta * (maxZ-minZ)/(double)nbinsZ)
00665                                     * ((binR+1)*DeltaR + m_p->minR)*((binR+1)*DeltaR + m_p->minR) - (binR*DeltaR + m_p->minR)*(binR*DeltaR + m_p->minR)/2.)/cm3;  // Delta_r^2 / 2
00666 
00667             if(k==0) { // XY and XZ energy deposit for every particles
00668                 analysisManager->FillH2( 0, CalculateCartesianPos(Radius, Theta, Zpos).x() + m_p->origin_PolarMesh[0],
00669                                             CalculateCartesianPos(Radius, Theta, Zpos).y() + m_p->origin_PolarMesh[1], value/eV  / (CubicVolume * m_p->nbinsZ));
00670                 analysisManager->FillH2(10, CalculateCartesianPos(Radius, Theta, Zpos).x() + m_p->origin_PolarMesh[0],
00671                                             CalculateCartesianPos(Radius, Theta, Zpos).z() + m_p->origin_PolarMesh[2], value/eV  / (CubicVolume * m_p->nbinsY));
00672 
00673                 analysisManager->FillNtupleFColumn( 2, 0,Radius);               // ID= 0 radial pos in mm
00674                 analysisManager->FillNtupleFColumn( 2, 1,Theta);                // ID= 1 Theta in degree
00675                 analysisManager->FillNtupleFColumn( 2, 2,Zpos);                 // ID= 2 PosZ in mm
00676                 analysisManager->FillNtupleDColumn( 2, 3,value/eV ); // ID= 3 deposited power to be multiplied by the beam power and divided by the number of source particles
00677                 analysisManager->AddNtupleRow(2);
00678             }
00679             if(k==3 || k==4) // Energy deposit for proton or gamma
00680                 analysisManager->FillH2(k, CalculateCartesianPos(Radius, Theta, Zpos).x() + m_p->origin_PolarMesh[0],
00681                                             CalculateCartesianPos(Radius, Theta, Zpos).y() + m_p->origin_PolarMesh[1], value/eV  / CubicVolume / m_p->nbinsZ);
00682             else if (k==1) { // neutron flux
00683                 analysisManager->FillH2(1, CalculateCartesianPos(Radius, Theta, Zpos).x() + m_p->origin_PolarMesh[0],
00684                                             CalculateCartesianPos(Radius, Theta, Zpos).y() + m_p->origin_PolarMesh[1], value/(1/cm2)  /e_SI / m_p->nbinsZ);
00685                 analysisManager->FillH2(6, CalculateCartesianPos(Radius, Theta, Zpos).x() + m_p->origin_PolarMesh[0],
00686                                             CalculateCartesianPos(Radius, Theta, Zpos).z() + m_p->origin_PolarMesh[2], value/(1/cm2)  /e_SI / m_p->nbinsY);
00687             }
00688             else if (k==2) { // gamma flux
00689                 analysisManager->FillH2( 2, CalculateCartesianPos(Radius, Theta, Zpos).x() + m_p->origin_PolarMesh[0],
00690                                             CalculateCartesianPos(Radius, Theta, Zpos).y() + m_p->origin_PolarMesh[1], value/(1/cm2)  /e_SI / m_p->nbinsZ);
00691                 analysisManager->FillH2(11, CalculateCartesianPos(Radius, Theta, Zpos).x() + m_p->origin_PolarMesh[0],
00692                                             CalculateCartesianPos(Radius, Theta, Zpos).z() + m_p->origin_PolarMesh[2], value/(1/cm2)  /e_SI / m_p->nbinsY);
00693             }
00694         }
00695     }
00696 }
00697 
00698 void RunAction::ReadHexagonalMesh(const G4Run* run, int /*Nprotons*/, double /*dbl_Ibeam*/) {
00699     // Mesh cubic volume
00700     G4double CubicVolume = (3.*sqrt(3.)/8. * m_p->hexPitch * m_p->hexPitch * (m_p->maxZ-m_p->minZ)/m_p->nbinsZ)/cm3;
00701 
00702     // Pitch of the binning
00703     G4double r = m_p->hexPitch * 0.5;
00704     G4double d = std::sqrt(3) * r;
00705     G4double DeltaZ = (m_p->maxZ-m_p->minZ)/(double)m_p->nbinsZ; // Z pitch = Z box length
00706 
00707     auto analysisManager = G4AnalysisManager::Instance();
00708     const Run* theRun = (const Run*)run;
00709     G4double Xpos, Ypos, Zpos;
00710     int nBinsY = m_p->nbinsHexMaxY-m_p->nbinsHexMinY+1;
00711 
00712     // For each scored quantity of the sensitive detector
00713     for (int k=0; k<theRun->GetNumberOfSD(); k++) {
00714 
00715         const G4THitsMap<G4double> map = theRun->GetFullMap(k); // Get the 3D map of the scoring quantity
00716         std::map<G4int,G4double*>::iterator itr = map.GetMap()->begin();
00717         std::map<G4int,G4double*>::iterator itr_end = map.GetMap()->end();
00718         G4double value = 0.;
00719         G4int key = 0, nx, ny, nz;
00720 
00721 //         for(; itr != map.GetMap()->end(); ++itr) {
00722         for(; itr != itr_end; ++itr) {
00723             key   = (itr->first);   // key of the mesh
00724             value = *(itr->second);
00725 
00726             // Bin number for each axis
00727             nx = (int)(key/(int)(nBinsY*m_p->nbinsZ));
00728             ny = (int)(key/(int)(m_p->nbinsZ) - nx * nBinsY);
00729             nz = key - ny * m_p->nbinsZ - nx * nBinsY * m_p->nbinsZ;
00730 
00731             // Position of the bin number
00732             Xpos =  (double)(2 * (nx+m_p->nbinsHexMinX) + (int)(ny+m_p->nbinsHexMinY)%2) * r;
00733             Ypos =  (double)(ny+m_p->nbinsHexMinY) * d;
00734             Zpos = ((double)(nz) + 0.5) * DeltaZ + m_p->minZ;
00735 
00736             if(k==0) { // energy deposit for every particles
00737 
00738                 // Fill 2D with the projected mean value
00739                 analysisManager->FillH2( 0, Xpos/mm, Ypos/mm, value/eV  / (CubicVolume * m_p->nbinsZ));
00740                 analysisManager->FillH2(10, Xpos/mm, Zpos/mm, value/eV  / (CubicVolume * nBinsY));
00741 
00742                // Fill the ntuple to get a 3D mesh
00743                 analysisManager->FillNtupleFColumn( 2, 0, Xpos/mm);              // ID= 0 radial pos in mm
00744                 analysisManager->FillNtupleFColumn( 2, 1, Ypos/mm);              // ID= 1 Theta in rad
00745                 analysisManager->FillNtupleFColumn( 2, 2, Zpos/mm);              // ID= 2 PosZ in mm
00746                 analysisManager->FillNtupleDColumn( 2, 3, value/eV ); // ID= 3 Kinetic energy in eV
00747                 analysisManager->AddNtupleRow(2);
00748             }
00749             if(k==3 || k==4) // Energy deposit for proton or gamma
00750                 analysisManager->FillH2(k, Xpos/mm, Ypos/mm, value/eV  / CubicVolume / m_p->nbinsZ);
00751             else if (k==1) { // neutron flux
00752                 analysisManager->FillH2(1, Xpos/mm, Ypos/mm, value/(1/cm2)  / m_p->nbinsZ);
00753                 analysisManager->FillH2(6, Xpos/mm, Zpos/mm, value/(1/cm2)  / nBinsY);
00754             }
00755             else if (k==2) { // gamma flux
00756                 analysisManager->FillH2( 2, Xpos/mm, Ypos/mm, value/(1/cm2)  / m_p->nbinsZ);
00757                 analysisManager->FillH2(11, Xpos/mm, Zpos/mm, value/(1/cm2)  / nBinsY);
00758             }
00759         }
00760     }
00761 }
