00773 void RunAction::UseABLAmodel() {
00774       G4cout<< " **************************************************** "<<G4endl;
00775       G4cout<< " ******** Use of ABLA v3 de-excitation model ******** "<<G4endl;
00776       G4cout<< " **************************************************** "<<G4endl;
00777       // Get hold of pointers to the INCL++ model interfaces
00778       std::vector<G4HadronicInteraction*> interactions =
00779               G4HadronicInteractionRegistry::Instance()->FindAllModels( G4INCLXXInterfaceStore::GetInstance()->getINCLXXVersionName());
00780       for(std::vector<G4HadronicInteraction*>::const_iterator iInter=interactions.begin(),e=interactions.end();iInter!=e; ++iInter)
00781       {
00782           G4INCLXXInterface*theINCLInterface = static_cast<G4INCLXXInterface*>(*iInter);
00783           if(theINCLInterface)
00784           {
00785               // Instantiate the ABLA model
00786               G4HadronicInteraction*interaction = G4HadronicInteractionRegistry::Instance()->FindModel("ABLA");
00787               G4AblaInterface*theAblaInterface =static_cast<G4AblaInterface*>(interaction);
00788               if(!theAblaInterface)
00789               {
00790                   theAblaInterface = new G4AblaInterface;
00791                   // Couple INCL++ to ABLA
00792                   G4cout << "Coupling INCLXX to ABLA" << G4endl;
00793                   theINCLInterface->SetDeExcitation(theAblaInterface);
00794               }
00795           }
00796       }
00797 }
