00811 // Calculate the cartesian position from polar oordinates
00812 // Theta is in degree
00813 G4ThreeVector RunAction::CalculateCartesianPos(double Radius, double Theta, double Zpos) {
00814     if (m_p->polarAxis == "Y") { // if Y-axis (cartesian) corresponds to the Z-axis in polar coordinates
00815         return G4ThreeVector(Radius/mm * std::sin(Theta*CLHEP::pi/180.), Zpos, Radius/mm * std::cos(Theta*CLHEP::pi/180.));
00816     }
00817     else if (m_p->polarAxis == "Z") { // if Z-axis (cartesian) corresponds to the Z-axis in polar coordinates
00818         return G4ThreeVector(Radius/mm * std::cos(Theta*CLHEP::pi/180.), Radius/mm * std::sin(Theta*CLHEP::pi/180.), Zpos);
00819     }
00820     else { // Default is X-axis in cartesian coordinates corresponding to Z-axis in polar coordinates
00821         return G4ThreeVector(Zpos, Radius/mm * std::cos(Theta*CLHEP::pi/180.), Radius/mm * std::sin(Theta*CLHEP::pi/180.));
00822     }
00823 }
