00038 // Construction of the logical volumes for cladding and Fuel and water in the active area
00039 // 1 Hexagon + 4 concentric cylinders for WWR-M5 (or VVR-M5)
00041 void TMXFuel::Build_WWR_M5(G4double RotAnglePolygon, TMXInputParameters* p,
00042                                   G4LogicalVolume* G4LogicCladdingUnion    , G4Material* G4CladdingMaterial,
00043                                   G4LogicalVolume* G4LogicWaterFuelCladding, G4Material* G4WaterFuelMaterial,
00044                                   G4LogicalVolume* G4LogicFuelUnion        , G4Material* G4FuelMaterial,
00045                                   std::vector<G4LogicalVolume*>& G4LogicFuel,
00046                                   std::vector<std::string>& vec_LVname )
00047 {
00048   G4double dbl_width         = p->dbl_PolygoneWidth;        // width between 2 opposite sides, not the maximum of of the polygon
00049   G4double dbl_CladPitch     =    1.55*mm;                 // Distance between 2 cylinders
00050   G4double dbl_CladThickness =    1.25*mm;                 // Total thickness of cladding (with fuel inside)
00051   G4double dbl_Clad2diam     =    27.9*mm;                 // External diameter of cladding 2
00052   G4double dbl_WallThickness =    0.33*mm;              // Thickness of cladding wall only  // Or 0.43 ??
00053 
00054   // Hexagon/cylinders parameters
00055   G4int int_Nsides = 6; // Side   number for the polygon
00056   G4int int_NZplan = 2; // Z-plan number for the polygon
00057   G4double* dbl_z_coord = new double[int_NZplan];
00058   G4double* dbl_rin     = new double[int_NZplan];
00059   G4double* dbl_rext    = new double[int_NZplan];
00060   G4double dbl_CladRint;                                   // internal radius
00061   G4double dbl_CladRext;                                   // external radius
00062   G4double dbl_active_l[2]   = {2 * p->CladdingZ, 2 * p->FuelZ}; // Height for Cladding or Fuel
00063   std::string str_name[2]    = {"Cladding", "Fuel"};
00064   
00065   
00066   for( int k = 0; k < 2; ++k) { // k=0 >> Cladding and k=1 >> Fuel
00067       
00068     G4double dbl_max_ext_width = dbl_width - 2 * k * dbl_WallThickness;
00069     G4double dbl_max_int_width = dbl_max_ext_width - 2* (dbl_CladThickness - 2 * k * dbl_WallThickness);
00070     
00071     // Parameter definition to implement hexagon geometry
00072     for(int i=0; i<int_NZplan; ++i)  {
00073         dbl_z_coord[i] = (i-0.5) * dbl_active_l[k];
00074         dbl_rin[i]  = dbl_max_int_width/2;
00075         dbl_rext[i] = dbl_max_ext_width/2;
00076     }
00077 
00078     // External cladding (number 1) = hexagon
00079     G4Polyhedra* cladding1 = new G4Polyhedra("sCladding1", // Name
00080                         RotAnglePolygon,                   // Phi start
00081                         twopi*5/4.,                        // Phi total
00082                         int_Nsides,                        // Number of sides
00083                         int_NZplan,                        // Number of Z-planes
00084                         dbl_z_coord,                       // coordinates for Z-planes
00085                         dbl_rin,                           // coordinates for internal radius
00086                         dbl_rext);                         // coordinates for exernal  radius
00087     
00088     // Four concentric cylinders
00089     G4Tubs* CylindCladdingFuel[5]={0};
00090     G4UnionSolid* CladdingFuelUnion[5]={0};
00091     for(int i = 0; i<5; ++i) {
00092         dbl_CladRext = dbl_Clad2diam/2 - i*( dbl_CladPitch + dbl_CladThickness) - k * dbl_WallThickness;
00093         dbl_CladRint = dbl_CladRext - dbl_CladThickness + 2 * k * dbl_WallThickness;
00094         
00095         std::string str_CladName = std::string("s") + str_name[k].c_str() + std::to_string((int)(i+2));
00096         CylindCladdingFuel[i] = new G4Tubs(str_CladName.c_str(), dbl_CladRint, dbl_CladRext, 0.5*dbl_active_l[k], 0., twopi);
00097         
00098         if( i == 0) // Union of the first cylinder and the hexagon
00099             CladdingFuelUnion[i] = new G4UnionSolid((str_name[k] + "1+2").c_str(), cladding1, CylindCladdingFuel[i]);
00100         else {      // Union of the previous unions with the last cylinder
00101             std::string str_UnionName = str_name[k].c_str() + std::to_string((int)(i-1)) + std::string("+") + std::to_string((int)i);
00102             CladdingFuelUnion[i] = new G4UnionSolid(str_UnionName.c_str(), CladdingFuelUnion[i-1], CylindCladdingFuel[i]);
00103         }
00104     }
00105     
00106     if( k==0 ) {
00107         G4LogicCladdingUnion = new G4LogicalVolume(CladdingFuelUnion[4], G4CladdingMaterial,"lCladdingUnion");
00108         vec_LVname.push_back(G4LogicCladdingUnion->GetName());
00109         
00110         // Mother volume ( = entire rod) filled of water
00111         G4double r_water[2] = {0., 0.};
00112         G4double r_water_ext[2] = {dbl_rext[0]+p->dbl_rRodsGap/2., dbl_rext[0]+p->dbl_rRodsGap/2.};
00113         G4Polyhedra* WaterFuel  = new G4Polyhedra("sWaterFuel", RotAnglePolygon, twopi*5/4., int_Nsides, int_NZplan, dbl_z_coord, r_water, r_water_ext);
00114         G4LogicWaterFuelCladding = new G4LogicalVolume(WaterFuel, G4WaterFuelMaterial,"lWaterFuelCladding");
00115         vec_LVname.push_back(G4LogicWaterFuelCladding->GetName());
00116     }
00117     else if( k==1 ) {
00118         G4LogicFuel.clear();
00119         G4LogicFuelUnion   = new G4LogicalVolume(CladdingFuelUnion[4] , G4FuelMaterial ,"lFuelUnion");    
00120         vec_LVname.push_back(G4LogicFuelUnion->GetName());
00121         G4LogicFuel.push_back( new G4LogicalVolume(cladding1            , G4FuelMaterial ,"lFuel" + std::to_string((int)G4LogicFuel.size()) ) );
00122         vec_LVname.push_back(G4LogicFuel[G4LogicFuel.size()-1]->GetName());
00123         for(int i = 0; i<5; ++i) {
00124             G4LogicFuel.push_back( new G4LogicalVolume(CladdingFuelUnion[i] , G4FuelMaterial ,"lFuel" + std::to_string((int)G4LogicFuel.size()) ) );
00125             vec_LVname.push_back(G4LogicFuel[G4LogicFuel.size()-1]->GetName());
00126         }
00127     }
00128   }
00129   
00130   delete [] dbl_z_coord;
00131   delete [] dbl_rin;
00132   delete [] dbl_rext;
00133 }
